INSERT INTO `tbl_danhmuc` (`id_danhmuc`, `ten_danhmuc`, `category_status`, `category_detail`, `category_created_time`, `category_last_updated`) VALUES
(1, 'Tai Nghe', 1, '<h2>Tai nghe không dây Xiaomi</h2>\r\n\r\n<p>\r\nTai nghe không dây Xiaomi là dòng <strong>True Wireless Earbuds</a></strong> \r\nvà tai nghe bluetooth chất lượng cao, được yêu thích nhờ thiết kế gọn nhẹ, âm thanh sống động và giá thành hợp lý. \r\nDòng sản phẩm nổi bật gồm:\r\n</p>\r\n\r\n<ul>\r\n  <li>Redmi Bud 6</li>\r\n  <li>Redmi Buds 6 Lite</li>\r\n  <li>Redmi Buds 6 Active</li>\r\n  <li>Xiaomi Type-C Earphones</li>\r\n  <li>Redmi Buds 6 Pro</li>\r\n  <li>Xiaomi Buds 5</li>\r\n\r\n</ul>\r\n\r\n<h2><strong>Tính năng nổi bật</strong></h2>\r\n\r\n<ul>\r\n  <li>Kết nối <strong>Bluetooth 5.0</strong> nhanh chóng, ổn định</li>\r\n  <li>Âm thanh chất lượng cao với codec AAC / SBC</li>\r\n  <li>Chống ồn chủ động (ANC) trên một số model</li>\r\n  <li>Điều khiển cảm ứng tiện lợi: play/pause, chuyển bài, tăng giảm âm lượng</li>\r\n  <li>Micro kép lọc ồn, đàm thoại rõ ràng</li>\r\n  <li>Thiết kế in-ear ôm tai, đeo êm, không rơi</li>\r\n  <li>Thời lượng pin lên tới 6–7 giờ mỗi lần sạc, tổng cộng 24–28 giờ với hộp sạc</li>\r\n  <li>Kháng nước IPX4/IPX5, an tâm sử dụng khi tập luyện thể thao</li>\r\n  <li>Tự động dừng/phát nhạc khi tháo/hở một bên tai nghe</li>\r\n  <li>Hỗ trợ voice assistant (Google Assistant, Siri)</li>\r\n  <li>Chế độ chơi game độ trễ thấp (Low Latency Mode) trên model cao cấp</li>\r\n</ul>\r\n', 1748332670, 1748332670),
(2, 'Đồng hồ', 1, '<h2>Đồng hồ thông minh Xiaomi</h2>\r\n\r\n<p>\r\nĐồng hồ thông minh Xiaomi là dòng <strong></strong> \r\nvà vòng theo dõi sức khỏe bán chạy nhất, nổi bật với thiết kế thanh lịch, pin bền, kết nối ổn định. \r\nCác model nổi bật gồm:\r\n</p>\r\n\r\n<ul>\r\n  <li>Redmi Watch 5</li>\r\n  <li>Xiaomi Watch S4</li>\r\n  <li>Xiaomi Smart Band 9 Active</li>\r\n  <li>Xiaomi Smart Band 9 Pro</li>\r\n  <li>Redmi Watch 5 Lite</li>\r\n  <li>Redmi Watch 5 Active</li>\r\n  <li>Xiaomi Watch 2</li>\r\n</ul>\r\n\r\n<h2><strong>Tính năng nổi bật</strong></h2>\r\n\r\n<ul>\r\n  <li>Theo dõi nhịp tim 24/7 với cảm biến PPG chính xác</li>\r\n  <li>Đo nồng độ oxy trong máu SpO₂ ngay lập tức</li>\r\n  <li>Hơn 100 chế độ luyện tập thể thao tự động nhận dạng</li>\r\n  <li>GPS tích hợp (trên model cao cấp) cho bản đồ đường chạy</li>\r\n  <li>Màn hình cảm ứng AMOLED sắc nét, độ sáng cao</li>\r\n  <li>Chống nước 5ATM, an tâm bơi lội và sinh hoạt hàng ngày</li>\r\n  <li>Thông báo cuộc gọi, tin nhắn, ứng dụng ngay trên cổ tay</li>\r\n  <li>Điều khiển phát nhạc và chụp ảnh từ xa qua Bluetooth</li>\r\n  <li>Thời lượng pin lên tới 14–20 ngày tùy mức sử dụng</li>\r\n  <li>Ứng dụng Mi Fitness (Zepp Life) đồng bộ dữ liệu sức khỏe toàn diện</li>\r\n</ul>\r\n', 1748334849, 1748334849),
(3, 'Phụ Kiện', 1, '<h2>Phụ kiện Xiaomi</h2>\r\n\r\n<p>\r\nPhụ kiện Xiaomi bao gồm các sản phẩm <strong></strong> chất lượng cao, thiết kế tiện lợi và giá cả phải chăng. \r\nDòng phụ kiện này được ưa chuộng nhờ khả năng tương thích rộng, độ bền tốt và nhiều lựa chọn đa dạng. Các sản phẩm nổi bật gồm:\r\n</p>\r\n\r\n<ul>\r\n  <li>Xiaomi Focus Pen (White)</li>\r\n  <li>Xiaomi Pad 7 / 7 Pro Cover </li>\r\n  <li>Xiaomi Pad 7 / 7 Pro Keyboard</li>\r\n  <li>Xiaomi Pad 7 / 7 Pro Focus Keyboard</li>\r\n  <li>Xiaomi 15 Ultra Photography Kit Legend Edition</li>\r\n\r\n</ul>\r\n\r\n<h2><strong>Tính năng nổi bật</strong></h2>\r\n\r\n<ul>\r\n  <li>Thiết kế gọn nhẹ, dễ dàng mang theo</li>\r\n  <li>Chất liệu nhựa ABS cao cấp, bền bỉ chống va đập</li>\r\n  <li>Tương thích đa dạng với smartphone, tablet và laptop</li>\r\n  <li>Công nghệ sạc nhanh (Quick Charge) hỗ trợ lên đến 18 W</li>\r\n  <li>Cổng USB‑C và USB‑A linh hoạt cho nhiều thiết bị</li>\r\n  <li>Khả năng sạc ngược (Power Bank có chức năng gửi điện)</li>\r\n  <li>Độ dài cáp tiêu chuẩn 1 m, lõi đồng nguyên chất cho truyền tải ổn định</li>\r\n  <li>Hỗ trợ sạc qua cổng Lightning (với adapter đi kèm)</li>\r\n  <li>Chứng nhận an toàn CE, FCC, RoHS</li>\r\n  <li>Giá thành hợp lý, dễ tìm mua trên các kênh online và cửa hàng Xiaomi</li>\r\n</ul>\r\n', 1748335299, 1748335299),
(4, 'Điện thoại', 1, '<h2>Điện thoại Xiaomi</h2>\n\n<p>\nĐiện thoại Xiaomi là dòng <strong></strong> bán chạy, nổi tiếng với cấu hình mạnh, thiết kế đẹp và mức giá rất cạnh tranh. \nCác model nổi bật nhất gồm:\n</p>\n\n<ul>\n  <li>Redmi Note 14</li>\n  <li>POCO X7 Pro</li>\n  <li>Xiaomi 14T</li>\n  <li>Xiaomi 14T Pro</li>\n  <li>POCO M6 Pro</li>\n  <li>POCO C75</li>\n  <li>Redmi A3x</li>\n  <li>Xiaomi 15 Ultra</li>\n  <li>Redmi A3</li>\n  <li>Redmi 14C</li>\n  <li>Redmi 13</li>\n  <li>Redmi Note 14 Pro+ 5G</li>\n  <li>Redmi Note 14 Pro</li>\n  <li>POCO X7 Pro</li>\n  <li>POCO C71</li>\n  <li>POCO M7 Pro 5G</li>\n  <li>Redmi Note 14 5G</li>\n  <li>Xiaomi 15</li>\n\n\n</ul>\n\n<h2><strong>Tính năng nổi bật</strong></h2>\n\n<ul>\n  <li>Màn hình AMOLED/IPS với tần số quét cao lên đến 120 Hz</li>\n  <li>Chip xử lý hiệu năng mạnh mẽ (Snapdragon/G95/Dimensity)</li>\n  <li>Hệ thống camera đa ống kính: chính 108 MP, góc rộng, macro, tele</li>\n  <li>Pin dung lượng lớn 4500–5000 mAh, hỗ trợ sạc nhanh 33–120 W</li>\n  <li>Bluetooth 5.2, NFC, hồng ngoại điều khiển thiết bị gia dụng</li>\n  <li>Cảm biến vân tay dưới màn hình hoặc cạnh bên</li>\n  <li>Giao diện MIUI thân thiện, nhiều tuỳ chỉnh, cập nhật thường xuyên</li>\n  <li>Âm thanh stereo, jack 3.5 mm trên một số model</li>\n  <li>Chống nước, bụi chuẩn IP53/IP68 (tùy model)</li>\n  <li>Hỗ trợ 5G, Wi‑Fi 6, GPS chính xác</li>\n  <li>Giá thành hợp lý, nhiều phiên bản ROM/RAM để lựa chọn</li>\n</ul>\n', 1748336152, 1748336152),
(5, 'Smart Tivi', 1, '<h2>Smart TV Xiaomi</h2>\r\n\r\n<p>\r\nSmart TV Xiaomi là dòng  \r\nvà Android TV giá tốt, nổi bật với chất lượng hình ảnh 4K HDR, giao diện mượt mà và tích hợp hệ sinh thái Xiaomi. \r\nCác model bán chạy nhất gồm:\r\n</p>\r\n\r\n<ul>\r\n  <li>Xiaomi TV A Pro 55\" 2025</li>\r\n  <li>Xiaomi TV A43\" FHD 2025</li>\r\n  <li>Xiaomi TV A Pro 43\" 2025</li>\r\n  <li>Xiaomi TV A 32\"</li>\r\n  <li>Xiaomi TV A 55\" 2025</li>\r\n  <li>Xiaomi TV A Pro 65\" 2025</li>\r\n\r\n</ul>\r\n\r\n<h2><strong>Tính năng nổi bật</strong></h2>\r\n\r\n<ul>\r\n  <li>Độ phân giải 4K UHD cùng công nghệ HDR10+ cho màu sắc sống động</li>\r\n  <li>Hệ điều hành Android TV với Google Assistant, Chromecast tích hợp</li>\r\n  <li>Thiết kế viền mỏng, chân đế kim loại chắc chắn, sang trọng</li>\r\n  <li>Công nghệ âm thanh Dolby Audio và DTS-HD cho trải nghiệm xem phim chân thực</li>\r\n  <li>Trí tuệ nhân tạo AI Picture & AI Sound tự động tối ưu hình ảnh và âm thanh</li>\r\n  <li>Hỗ trợ cấp nguồn cho thiết bị qua cổng HDMI‑CEC</li>\r\n  <li>Điều khiển từ xa Bluetooth tích hợp micro, hỗ trợ tìm kiếm giọng nói</li>\r\n  <li>Chế độ chơi game Game Mode với độ trễ thấp (low latency)</li>\r\n  <li>Ứng dụng Mi Home đồng bộ điều khiển IoT trong hệ sinh thái Xiaomi</li>\r\n  <li>Cổng kết nối đầy đủ: HDMI 2.1, USB 2.0, Ethernet, Optical Out</li>\r\n</ul>\r\n', 1748337198, 1748337198),
(6, 'Máy tính bảng', 1, '<h2>Máy tính bảng Xiaomi</h2>\r\n\r\n<p>\r\nMáy tính bảng Xiaomi là dòng \r\nvà iPad giá phải chăng, nổi bật với màn hình lớn, cấu hình tốt và pin trâu. \r\nCác model bán chạy nhất gồm:\r\n</p>\r\n\r\n<ul>\r\n  <li>Xiaomi Pad 7</li>\r\n  <li>POCO Pad</li>\r\n  <li>Redmi Pad Pro</li>\r\n  <li>Redmi Pad SE 8.7 4G</li>\r\n  <li>Redmi Pad SE 8.7 WiFi</li>\r\n  <li>Redmi Pad SE</li>\r\n  <li>Xiaomi Pad 7 Pro</li>\r\n\r\n</ul>\r\n\r\n<h2><strong>Tính năng nổi bật</strong></h2>\r\n\r\n<ul>\r\n  <li>Màn hình LCD/IPS độ phân giải cao, tần số quét 90–120 Hz</li>\r\n  <li>Chip hiệu năng mạnh mẽ: Snapdragon 860/870/MediaTek Helio</li>\r\n  <li>Pin dung lượng lớn 8000–10000 mAh, hỗ trợ sạc nhanh</li>\r\n  <li>Hỗ trợ bút cảm ứng Xiaomi Smart Pen (trên Pad 5 Pro)</li>\r\n  <li>Hệ thống loa stereo cho chất lượng âm thanh sống động</li>\r\n  <li>Camera trước 8 MP, camera sau 13 MP cho video call và chụp ảnh cơ bản</li>\r\n  <li>Kết nối Wi‑Fi 6, Bluetooth 5.2, USB‑C</li>\r\n  <li>MIUI for Pad tối ưu hóa giao diện tablet</li>\r\n  <li>Chế độ đa nhiệm chia đôi màn hình, cửa sổ nổi tiện lợi</li>\r\n  <li>Thiết kế kim loại nguyên khối, mỏng nhẹ, cầm thoải mái</li>\r\n</ul>\r\n', 1748337350, 1748337350);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `id_danhmuc`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `giamgia`, `giadagiam`, `daban`, `averange_rating`, `trangthaisp`, `tomtat`, `noidung`, `created_time`, `last_updated`) VALUES
(1, 1, 'Redmi Buds 6', '1290000', 30, 'Redmi_Buds_6.png', 30, 903000, 10, 4, 1, '<ul>\n    <li> Thiết kế gọn nhẹ, vừa vặn tai, mang lại cảm giác thoải mái khi đeo lâu</li>\n    <li> Driver 10mm cho âm thanh sống động, chi tiết và mạnh mẽ</li>\n    <li> Tính năng chống ồn chủ động (ANC) hiệu quả, giảm tiếng ồn môi trường</li>\n    <li> Micro kép với AI lọc ồn giúp cuộc gọi rõ ràng hơn</li>\n    <li> Thời lượng pin lên đến 30 giờ với hộp sạc – nghe nhạc suốt ngày dài</li>\n</ul>\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\n  <h2 class=\"ksp-title\">Đặc điểm nổi bật</h2>\n  <div class=\"ksp-content p-2 mb-4\">\n    <div class=\"cps-content-introduction\">\n      <p style=\"text-align: justify\">\n        <strong>Tai nghe Xiaomi Redmi Buds 6</strong> có trang bị trình\n        <strong>điều khiển 12.4mm</strong>, pin tai nghe cho thời gian sử dụng\n        đến&nbsp;26 giờ khi&nbsp;tích hợp công nghệ ENC lọc tiếng ồn. Mẫu tai\n        nghe Bluetooth này sử dụng chuẩn kết nối\n        <strong>Bluetooth 5.4</strong> với phạm vi <strong>10m</strong>, hỗ trợ\n        điều khiển cảm ứng chạm, sạc qua cổng Type-C. Hộp sạc có kích thước\n        61.01 x 51.71 x 24.80&nbsp;mm, tai nghe 31.13 x 21.34 x 23.5 mm với khối\n        lượng 4g.\n      </p>\n    </div>\n  </div>\n  <div id=\"cpsContentSEO\">\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"vi-sao-nen-mua-tai-nghe-xiaomi-redmi-buds-6-su-dung\"\n    >\n      <strong>Vì sao nên mua tai nghe&nbsp;Xiaomi Redmi Buds 6 sử dụng?</strong>\n    </h2>\n    <p style=\"text-align: justify\">\n      Tai nghe không dây Redmi Buds 6 có điểm gì nổi bật đáng sở hữu, hãy cùng\n      đánh giá nhanh:\n    </p>\n    <ul>\n      <li>\n        <p>\n          <strong>Công nghệ chống ồn chủ động</strong>: Hỗ trợ đàm thoại rõ nét,\n          giảm ồn đến 99.6%\n        </p>\n      </li>\n      <li>\n        <p>\n          <strong>Trình điều khiển kép cùng âm thanh vòm 360 độ</strong>: Trải\n          nghiệm âm thanh chất lượng cao\n        </p>\n      </li>\n      <li>\n        <p>\n          <strong>Pin&nbsp;475mAh và&nbsp;54mAh</strong>: Cho tổng thời gian sử\n          dụng có thể đến 42 giờ\n        </p>\n      </li>\n      <li>\n        <p>\n          <strong>3 Micro cùng tính năng chống ồn</strong>: Nâng cao chất lượng\n          đàm thoại\n        </p>\n      </li>\n    </ul>\n    <p>\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-4.jpg\"\n        alt=\"Vì sao nên mua tai nghe Xiaomi Redmi Buds 6 sử dụng\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"so-sanh-tai-nghe-xiaomi-redmi-buds-6-voi-redmi-buds-5\"\n    >\n      <strong\n        >So sánh tai nghe&nbsp;Xiaomi Redmi Buds 6 với Redmi Buds 5</strong\n      >\n    </h2>\n    <p style=\"text-align: justify\">\n      Xiaomi Redmi Buds 6 là dòng tai nghe Xiaomi mới ra mắt cuối năm 2024. Vậy\n      so với thế hệ Buds 5 trước đó, tai nghe không dây mới này có gì khác biệt?\n    </p>\n    <table border=\"0\">\n      <tbody>\n        <tr>\n          <td style=\"text-align: center\">\n            <p><strong>Tiêu chí</strong></p>\n          </td>\n          <td style=\"text-align: center\">\n            <p><strong>Xiaomi Redmi Buds 6</strong></p>\n          </td>\n          <td style=\"text-align: center\">\n            <p><strong>Xiaomi Redmi Buds 5</strong></p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Màu sắc</p></td>\n          <td style=\"text-align: center\"><p>Trắng - Xanh - Đen</p></td>\n          <td style=\"text-align: center\">\n            <p>Đen -&nbsp;Trắng -&nbsp;Xanh bầu trời</p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Màng loa</p></td>\n          <td style=\"text-align: center\">\n            <p>Dynamic Titanium&nbsp;12.4mm</p>\n            <p>Micro Piezoelectric&nbsp;5.5mm</p>\n          </td>\n          <td style=\"text-align: center\"><p>Dynamic driver 12,4mm</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Chống ồn</p></td>\n          <td style=\"text-align: center\">\n            <p>Chống ồn ba cấp độ</p>\n            <p>Noise Cancellation / Transparency / Off</p>\n          </td>\n          <td style=\"text-align: center\">\n            <p>Chống ồn ba cấp độ</p>\n            <p>Chống ồn sâu -&nbsp; Chống ồn cân bằng - Chống ồn nhẹ</p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Kháng nước</p></td>\n          <td style=\"text-align: center\"><p>IP54</p></td>\n          <td style=\"text-align: center\"><p>IP54</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Điều khiển</p></td>\n          <td style=\"text-align: center\"><p>Điều khiển cảm ứng chạm</p></td>\n          <td style=\"text-align: center\"><p>Điều khiển bằng cảm ứng</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Kết nối&nbsp;</p></td>\n          <td style=\"text-align: center\"><p>Bluetooth®5.4</p></td>\n          <td style=\"text-align: center\"><p>Bluetooth® 5.3</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Pin</p></td>\n          <td style=\"text-align: center\">\n            <p>475mAh(Case sạc), 54mAh x 2(Tai nghe)</p>\n          </td>\n          <td style=\"text-align: center\">\n            <p>480mAh(Case sạc), 54mAh x 2(Tai nghe)</p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Trọng lượng</p></td>\n          <td style=\"text-align: center\">\n            <p>5.0g(Tai nghe)/ 43.2g(Tai nghe và hộp sạc)</p>\n          </td>\n          <td style=\"text-align: center\">\n            <p>5.3g(Tai nghe)/ 42g(Tai nghe và hộp sạc)</p>\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <h2\n      id=\"tai-nghe-xiaomi-redmi-buds-6-trai-nghiem-am-thanh-manh-me-chan-thuc-trong-tam-tay\"\n      style=\"scroll-margin: 154px\"\n    >\n      <strong\n        >Tai nghe Xiaomi Redmi Buds 6 - Trải nghiệm âm thanh mạnh mẽ, chân thực\n        trong tầm tay</strong\n      >\n    </h2>\n    <p style=\"text-align: justify\">\n      Redmi Buds 6 là thiết bị âm thanh mới của Xiaomi, đã thành công gây ấn\n      tượng mạnh mẽ nhờ vào chất âm đỉnh cao. Kết hợp với thiết kế gọn nhẹ và\n      tính năng hiện đại, sản phẩm này là lựa chọn tuyệt vời để nâng tầm trải\n      nghiệm âm nhạc mọi lúc, mọi nơi.\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"chat-am-manh-me-va-phong-phu-voi-driver-12-4mm\"\n    >\n      <strong>Chất âm mạnh mẽ và phong phú với driver&nbsp;12.4mm</strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Tai nghe không dây Xiaomi Redmi Buds 6 được trang bị driver âm\n      thanh&nbsp;12.4mm để mang đến cho người dùng trải nghiệm âm thanh sống\n      động. Với kích thước driver lớn, tai nghe dễ dàng tái hiện rõ ràng từng\n      giai điệu, từ âm bass mạnh mẽ đến các dải mid và treble rõ ràng, tinh tế.\n      Sự kết hợp này không những giúp người dùng cảm nhận được độ sâu của âm\n      nhạc mà còn mang lại trải nghiệm nghe phong phú hơn.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-5.jpg\"\n        alt=\"Công suất tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"ngoai-hinh-toi-gian-sieu-gon-nhe-da-dang-mau-sac\"\n    >\n      <strong>Ngoại hình tối giản siêu gọn nhẹ, đa dạng màu sắc</strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Tai nghe Redmi Buds 6 còn được lòng khách hàng bởi kích thước nhỏ gọn,\n      khối lượng nhẹ với tai nghe chỉ nặng 4g. Thiết kế tai nghe được tối ưu hóa\n      để phù hợp với nhiều hình dạng tai khác nhau, đảm bảo độ bám tốt mà vẫn\n      thoải mái. Về màu sắc, Xiaomi mang đến sự đa dạng với nhiều tùy chọn như:\n      đen, hồng, xanh, trắng, giúp khách hàng dễ dàng lựa chọn theo sở thích cá\n      nhân.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-3_1.jpg\"\n        alt=\"Thiết kế tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Cụ thể hơn về ngoại hình, hộp sạc của Redmi Buds 6 rất nhỏ gọn, chỉ 49 x\n      48.6 x 23mm, dễ dàng cầm trên tay hay bỏ túi. Tổng thể thiết kế của thiết\n      bị mang lại sự tiện lợi, phù hợp cho người dùng di chuyển nhiều hoặc thích\n      phong cách hiện đại, tối giản. Một điểm đáng chú ý khác, thiết bị được chế\n      tác từ nhựa cao cấp, vừa bền bỉ vừa đảm bảo trải nghiệm nhẹ nhàng khi đeo.\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"micro-chat-luong-cao-voi-enc-luong-pin-den-6-30-gio\"\n    >\n      <strong\n        >Micro chất lượng cao với ENC, lượng pin đến 6 + 30 giờ&nbsp;</strong\n      >\n    </h3>\n    <p style=\"text-align: justify\">\n      Tai nghe Redmi Buds 6 còn được đánh giá cao nhờ sự tích hợp micro chất\n      lượng, hỗ trợ đàm thoại rõ ràng. Micro này hỗ trợ thu giọng nói sắc nét,\n      dù ở trong môi trường ồn ào hay nhiều tạp âm nhờ vào công nghệ khử ồn ENC.\n      Với điều này, tai nghe có thể đáp ứng tốt trong các tình huống đàm thoại\n      bất ngờ mà không lo lắng về chất lượng âm thanh.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-2_1.jpg\"\n        alt=\"Micro đàm thoại, pin tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Redmi Buds 6 có thời lượng pin dài 6 giờ khi được nạp đầy năng lượng, và\n      có thể kéo dài lên tới 30 giờ khi sử dụng với case sạc. Hộp sạc được trang\n      bị cổng sạc Type-C, cho tốc độ sạc nhanh và tiện lợi, giúp rút ngắn thời\n      gian chờ đợi của khách hàng. Do đó, sản phẩm sẽ là lựa chọn đáng đồng tiền\n      cho những ai cần một chiếc tai nghe có khả năng hoạt động liên tục trong\n      nhiều giờ liền.\n    </p>\n    <h3\n      id=\"micro-co-kha-nang-chong-gio-len-den-9m-s\"\n      style=\"scroll-margin: 154px\"\n    >\n      <strong>Micro có khả năng chống gió lên đến 9m/s</strong>\n    </h3>\n    <p>\n      Tai nghe Xiaomi Redmi Buds 6 được trang bị hệ thống 2 micro tiên tiến với\n      khả năng chống ồn hiệu quả, loại bỏ tạp âm từ môi trường xung quanh. Micro\n      có khả năng chống gió ấn tượng lên đến 9m/s, chất lượng âm thanh vượt trội\n      ngay cả khi người dùng đang di chuyển ngoài trời hoặc trong điều kiện thời\n      tiết có gió mạnh. Công nghệ này giúp nâng cao trải nghiệm gọi thoại, hỗ\n      trợ giao tiếp mượt mà và tự nhiên trong mọi hoàn cảnh.\n    </p>\n    <p>\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-6.jpg\"\n        alt=\"Đàm thoại tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h3\n      id=\"ket-noi-muot-ma-voi-chuan-bluetooth-5-4\"\n      style=\"scroll-margin: 154px\"\n    >\n      <strong>Kết nối mượt mà với chuẩn&nbsp;Bluetooth 5.4</strong>\n    </h3>\n    <p>\n      Tai nghe Xiaomi Redmi Buds 6 tích hợp công nghệ Bluetooth 5.4 có khả năng\n      kết nối không dây ổn định lên đến 10m. Điều đặc biệt là tai nghe có thể\n      kết nối đồng thời với 2 thiết bị, giúp người dùng dễ dàng chuyển đổi giữa\n      điện thoại, laptop hoặc máy tính bảng mà không cần phải ngắt kết nối.\n      Ngoài ra, Redmi Buds 6 hỗ trợ Google Fast Pair và ứng dụng Xiaomi Earbuds,\n      đảm bảo quá trình kết nối nhanh chóng và đơn giản.&nbsp;\n    </p>\n    <p>\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-7.jpg\"\n        alt=\"Kết nối tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h3 id=\"cong-nghe-chong-on-hybrid-active\" style=\"scroll-margin: 154px\">\n      <strong>Công nghệ chống ồn Hybrid Active</strong>\n    </h3>\n    <p>\n      Tai nghe Xiaomi Redmi Buds 6 được trang bị công nghệ chống ồn Hybrid\n      Active, mang đến khả năng loại bỏ tiếng ồn vượt trội với mức giảm lên đến\n      49dB tại tần số 2kHz. Công nghệ này hoạt động dựa trên sự kết hợp hoàn hảo\n      giữa chống ồn chủ động ANC và chống ồn thụ động PNC. Hệ thống ANC sử dụng\n      các micro tích hợp để thu tiếng ồn từ môi trường và tạo sóng nhiễu âm để\n      triệt tiêu những âm thanh đó.\n    </p>\n    <p>\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-9.jpg\"\n        alt=\"Công nghệ tai nghe không dây Xiaomi Redmi Buds 6\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p>\n      Redmi Buds 6 còn được trang bị ba chế độ chống ồn chủ động, tùy chỉnh linh\n      hoạt phù hợp từng môi trường. Chế độ nhẹ nhàng phù hợp với không gian yên\n      tĩnh như văn phòng làm việc hoặc thư viện, nơi chỉ cần loại bỏ những âm\n      thanh nhỏ. Chế độ trung bình được thiết kế để sử dụng tại những nơi công\n      cộng như quán cà phê hoặc cửa hàng đông đúc, nơi tiếng ồn thường xuyên ở\n      mức vừa phải.&nbsp;\n    </p>\n    <p>\n      Cuối cùng, chế độ mạnh mẽ phát huy tối đa hiệu quả trong các khu vực ngoài\n      trời ồn ào hoặc trên các phương tiện giao thông công cộng. Tai nghe không\n      dây Xiaomi Redmi Buds 6 còn đảm bảo giữ lại chất lượng âm thanh tự nhiên\n      và chi tiết. Người dùng có thể tận hưởng từng nốt nhạc, âm thanh hội thoại\n      rõ ràng mà không bị ảnh hưởng bởi tiếng động ồn ào từ môi trường xung\n      quanh.\n    </p>\n  </div>\n</div>\n', 1748347851, 1748347851),
(2, 1, 'Redmi Buds 6 Lite', '590000', 44, 'Redmi_Buds_6_Lite.png', 20, 472000, 13, 4, 1, '<ul>\r\n    <li>???? Thiết kế nhẹ nhàng, vừa vặn tai, mang lại sự thoải mái khi đeo cả ngày</li>\r\n    <li>???? Driver 10mm cho âm thanh rõ nét, sống động và cân bằng</li>\r\n    <li>???? Tích hợp chống ồn chủ động (ANC) giúp giảm tiếng ồn hiệu quả</li>\r\n    <li>???? Micro kép với công nghệ lọc tiếng ồn AI cho cuộc gọi trong trẻo</li>\r\n    <li>???? Thời lượng pin lên đến 24 giờ cùng hộp sạc tiện lợi</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\n  <h2 class=\"ksp-title\">Đặc điểm nổi bật</h2>\n  <div class=\"ksp-content p-2 mb-4\">\n    <div class=\"cps-content-introduction\">\n      <p style=\"text-align: justify\">\n        <strong>Tai nghe Bluetooth Xiaomi Redmi Buds 6 lite </strong> với độ trễ\n        mức <strong> 90ms </strong> và kết hợp với công nghệ\n        <strong> Bluetooth 5.4 </strong> sẽ cho phép đảm bảo kết nối ổn định khi\n        sử dụng. Trang bị bộ điều khiển động <strong> 12.4 mm </strong> mang đến\n        âm trầm sâu và các cuộc gọi sẽ giảm tiếng ồn nhờ vào micrô kép. Buds 6\n        Active còn có chứng nhận <strong> IPX4 </strong> về khả năng kháng nước\n        phù hợp cho thể thao ngoài trời.&nbsp;\n      </p>\n    </div>\n  </div>\n  <div id=\"cpsContentSEO\">\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"so-sanh-thong-so-xiaomi-redmi-buds-6-lite-voi-xiaomi-redmi-buds-4-lite\"\n    >\n      <strong\n        >So sánh thông số Xiaomi Redmi Buds 6 lite với&nbsp;Xiaomi Redmi Buds 4\n        lite</strong\n      >\n    </h2>\n    <p style=\"text-align: justify\">\n      Redmi Buds 6 lite sẽ là một sản phẩm thay thế chất lượng cho&nbsp;Redmi\n      Buds 4 lite. Vậy chính xác hai mẫu tai nghe này có gì khác biệt, cùng so\n      sánh chi tiết sau đây.\n    </p>\n    <table class=\"table table-bordered\">\n      <tbody>\n        <tr class=\"success\">\n          <td style=\"text-align: center\">\n            <p><strong>Thông số</strong></p>\n          </td>\n          <td style=\"text-align: center\">\n            <p><strong>Xiaomi Redmi Buds 6 lite</strong></p>\n          </td>\n          <td style=\"text-align: center\">\n            <p><strong>Xiaomi Redmi Buds 4 lite</strong></p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Màu sắc</p></td>\n          <td style=\"text-align: center\">\n            <p><strong>Đen - Trắng - Xanh</strong></p>\n          </td>\n          <td style=\"text-align: center\"><p>Đen - Trắng - Cam</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Trọng lượng</p></td>\n          <td style=\"text-align: center\"><p>4.2g mỗi tai nghe</p></td>\n          <td style=\"text-align: center\"><p>3,92g mỗi tai nghe</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Kích thước màng loa</p></td>\n          <td style=\"text-align: center\">\n            <p><strong>Titan 12.4mm</strong></p>\n          </td>\n          <td style=\"text-align: center\"><p>Driver động 12mm</p></td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Công nghệ</p></td>\n          <td style=\"text-align: center\">\n            <p><strong>Chống ồn chủ động Hybrid ANC 42dB</strong></p>\n            <p><strong>Chế độ trong suốt</strong></p>\n            <p><strong>Khử tiếng ồn 2 MIC AI</strong></p>\n            <p><strong>Chống ồn gió 6m/s</strong></p>\n          </td>\n          <td style=\"text-align: center\">Khử tiếng ồn cuộc gọi AI</td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Pin</p></td>\n          <td style=\"text-align: center\">\n            <p><strong>Tai nghe 45mAh&nbsp; sử dụng&nbsp;7giờ</strong></p>\n            <p><strong>Case sạc 480mAh sử dụng 38 giờ</strong></p>\n            <p>Cổng sạc USB-C</p>\n          </td>\n          <td style=\"text-align: center\">\n            <p>Tai nghe 35mAh sử dụng 5 giờ</p>\n            <p>Case sạc 320mAh sử dụng 20 giờ</p>\n            <p>Cổng sạc USB-C</p>\n          </td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Chuẩn kháng nước</p></td>\n          <td style=\"text-align: center\"></td>\n          <td style=\"text-align: center\">IP54</td>\n        </tr>\n        <tr>\n          <td style=\"text-align: center\"><p>Bluetooth</p></td>\n          <td style=\"text-align: center\">Bluetooth®5.3</td>\n          <td style=\"text-align: center\">Bluetooth®5.3</td>\n        </tr>\n      </tbody>\n    </table>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"tai-nghe-xiaomi-redmi-buds-6-lite-micro-kep-va-chung-nhan-cao-cap-ipx4\"\n    >\n      <strong\n        >Tai nghe Xiaomi Redmi Buds 6 lite - Micro kép và chứng nhận cao cấp\n        IPX4</strong\n      >\n    </h2>\n    <p style=\"text-align: justify\">\n      <strong> Tai nghe Redmi Buds 6 lite </strong> là sự lựa chọn cho những\n      người dùng thể hiện phong cách cá nhân. Với thiết kế nhỏ gọn, hạn chế\n      tiếng ồn hiệu quả sẽ đáp ứng các nhu cầu khác\n      <strong> nhau của bạn. </strong>\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"cong-nghe-am-thanh-an-tuong-va-do-tre-thap-90ms\"\n    >\n      <strong> Công nghệ âm thanh ấn tượng và độ trễ thấp 90ms </strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Tai nghe với độ trễ cực thấp ở mức 90ms sẽ đem lại trải nghiệm âm thanh\n      liền mạch và mượt mà hơn bao giờ hết. Bên cạnh đó, công nghệ Bluetooth 5.4\n      được tích hợp trong tai nghe sẽ đảm bảo kết nối ổn định khi sử dụng.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Buds/tai-nghe-bluetooth-xiaomi-redmi-buds-6-lite-2.jpg\"\n        alt=\"Âm thanh tai nghe Xiaomi Redmi Buds 6 lite\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Sản phẩm trang bị công nghệ âm thanh như Bass Boost, Vocal Boost,... cho\n      phép âm nhạc thêm sống động và rõ ràng. Từ đó tai nghe Bluetooth Xiaomi\n      Redmi Buds 6 lite đáp ứng được mọi nhu cầu nghe nhạc, chiến game cùng bạn\n      bè hay người thân.\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"nhieu-tuy-chon-mau-sac-ve-thiet-ke-gon-nhe\"\n    >\n      <strong> Nhiều tùy chọn màu sắc về thiết kế gọn nhẹ </strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Tai nghe có ba tùy chọn màu sắc như đen, trắng và xanh giúp người dùng đeo\n      vào tai trở nên thoải mái, ngay cả khi sử dụng trong thời gian dài mà\n      không lo bị rơi ra ngoài. Nhiều tùy chọn màu sẽ dễ dàng chọn màu sắc phù\n      hợp.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Buds/tai-nghe-bluetooth-xiaomi-redmi-buds-6-lite-3.jpg\"\n        alt=\"Thiết kế tai nghe Xiaomi Redmi Buds 6 lite\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Kích thước và trọng lượng gọn nhẹ của tai nghe Redmi Buds 6 lite không chỉ\n      giúp dễ dàng đem theo khi cần thiết mà còn lý tưởng cho những người cần di\n      chuyển thường xuyên. Bạn có thể an tâm về sự gọn nhẹ khi sử dụng.\n    </p>\n    <h3 style=\"scroll-margin: 154px\" id=\"micro-kep-va-chung-nhan-cao-cap-ipx4\">\n      <strong> Micro kép và chứng nhận cao cấp IPX4 </strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Trang bị bộ điều khiển động 12.4 mm mang đến âm trầm sâu và các cuộc gọi\n      sẽ giảm tiếng ồn nhờ vào micrô kép. Độ ồn bé chỉ 4m/s sẽ cho phép người\n      dùng có thể nghe và trò chuyện liền mạch mà không lo ồn ào khi sử\n      dụng.&nbsp;\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Buds/tai-nghe-bluetooth-xiaomi-redmi-buds-6-lite-1.jpg\"\n        alt=\"Đàm thoại trên tai nghe Xiaomi Redmi Buds 6 lite\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Ngoài ra, Xiaomi Redmi Buds 6 lite còn có chứng nhận IPX4 về khả năng\n      kháng nước,thích hợp cho các hoạt động thể thao ngoài trời hay các chuyến\n      đi du lịch. Với tính năng này, người dùng khi vận động đổ mồ hôi cũng\n      không bị hỏng.\n    </p>\n  </div>\n</div>\n', 1748350358, 1748350358),
(3, 1, 'Redmi Buds 6 Active', '490000', 32, 'Redmi_Buds_6_Active.png', 10, 441000, 10, 4, 1, '<ul>\n    <li> Thiết kế thể thao năng động, ôm sát tai, không lo rơi rớt khi vận động</li>\n    <li> Driver 10mm mang đến âm thanh mạnh mẽ, sống động, phù hợp mọi thể loại nhạc</li>\n    <li> Chống ồn chủ động (ANC) hiệu quả, giúp tập trung tuyệt đối khi nghe nhạc hoặc gọi điện</li>\n    <li> Micro kép với AI lọc ồn, đảm bảo giọng nói rõ ràng trong mọi cuộc gọi</li>\n    <li> Pin trâu lên đến 30 giờ cùng hộp sạc tiện lợi, đồng hành cùng bạn suốt ngày dài</li>\n</ul>\n', '<div class=\"rm-detail\">\n  <div id=\"gioithieu\">\n    <div class=\"product-description dtshow\" style=\"height: 400px\">\n      <p>\n        <span style=\"color: #000000\"\n          ><strong\n            >Trong thời đại công nghệ số hiện nay, âm nhạc và giải trí trở thành\n            một phần không thể thiếu trong cuộc sống hàng ngày. Việc sở hữu một\n            chiếc tai nghe không dây chất lượng không chỉ giúp người dùng tận\n            hưởng âm thanh đỉnh cao mà còn mang lại sự tiện lợi và phong cách.\n            Nhằm đáp ứng nhu cầu ngày càng cao của người tiêu dùng, Xiaomi đã\n            cho ra mắt sản phẩm Redmi Buds 6 Active – một chiếc tai nghe không\n            dây tích hợp nhiều tính năng vượt trội, thiết kế tinh tế, và khả\n            năng âm thanh tuyệt vời. Sản phẩm này hứa hẹn sẽ mang đến trải\n            nghiệm âm nhạc hoàn hảo cho mọi người dùng, từ những người yêu âm\n            nhạc đến các game thủ chuyên nghiệp.</strong\n          ></span\n        >\n      </p>\n      <p>\n        <img\n          decoding=\"async\"\n          class=\"aligncenter wp-image-30090 size-full wptangtoc-lazy entered gtloader\"\n          src=\"https://mihome.vn/wp-content/uploads/2024/08/z5761840567816_a68be36ebd10051f354f1818f209a163.jpg\"\n          alt=\"z5761840567816 a68be36ebd10051f354f1818f209a163\"\n          width=\"780\"\n          height=\"820\"\n          title=\"Tai nghe Xiaomi Redmi Buds 6 Active 14\"\n          fetchpriority=\"low\"\n          data-src=\"https://mihome.vn/wp-content/uploads/2024/08/z5761840567816_a68be36ebd10051f354f1818f209a163.jpg\"\n          data-sizes=\"(max-width: 1290px) 100vw, 1290px\"\n          data-ll-status=\"loaded\"\n          sizes=\"(max-width: 1290px) 100vw, 1290px\"\n        />\n      </p>\n      <h3>\n        <span id=\"Thiet_Ke_Hien_Dai_Va_Tinh_Te\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Thiết Kế Hiện Đại Và Tinh Tế</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          ><strong>Redmi Buds 6 Active</strong> được thiết kế với kiểu dáng nhỏ\n          gọn, hiện đại, phù hợp với mọi phong cách của người dùng. Với hộp sạc\n          nhỏ gọn, tai nghe có thể dễ dàng mang theo bên mình ở bất kỳ đâu, từ\n          phòng tập gym đến nơi làm việc. Sản phẩm có ba phiên bản màu sắc: đen,\n          xanh dương nhạt, và hồng nhạt, mang lại sự lựa chọn đa dạng cho người\n          dùng. Thiết kế này không chỉ thể hiện tính thẩm mỹ cao mà còn đảm bảo\n          sự thoải mái khi đeo trong thời gian dài.</span\n        >\n      </p>\n      \n      <h3>\n        <span id=\"Chat_Luong_Am_Thanh_Vuot_Troi\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Chất Lượng Âm Thanh Vượt Trội</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          >Điểm nhấn của <strong>Redmi Buds 6 Active</strong> chính là chất\n          lượng âm thanh vượt trội mà nó mang lại. Được trang bị\n          <strong>củ loa động 14,2 mm</strong>, tai nghe này cung cấp âm thanh\n          mạnh mẽ, sống động với độ biến dạng cực kỳ thấp. Nhờ sự tinh chỉnh của\n          Xiaomi Acoustic Lab, hiệu suất loa trầm được nâng cấp, giúp bạn cảm\n          nhận từng nốt nhạc một cách rõ ràng và chân thực nhất. Cho dù bạn đang\n          nghe nhạc, xem phim hay chơi game, Redmi Buds 6 Active đều đảm bảo\n          chất lượng âm thanh hoàn hảo.</span\n        >\n      </p>\n      <h3>\n        <span id=\"Kha_Nang_Giam_Tieng_On_Hieu_Qua\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Khả Năng Giảm Tiếng Ồn Hiệu Quả</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          >Một trong những tính năng nổi bật của\n          <strong>Redmi Buds 6 Active</strong> là khả năng giảm tiếng ồn vượt\n          trội nhờ công nghệ <strong>micro kép</strong>. Thuật toán khử tiếng ồn\n          hoạt động hiệu quả, giúp lọc tiếng ồn xung quanh một cách thông minh,\n          cho phép giọng nói được truyền tải rõ ràng trong các cuộc gọi hoặc khi\n          bạn đang sử dụng trợ lý giọng nói. Điều này đặc biệt hữu ích khi bạn\n          phải thực hiện cuộc gọi trong môi trường ồn ào hoặc ngoài trời.</span\n        >\n      </p>\n      <h3>\n        <span id=\"Thoi_Luong_Pin_An_Tuong\"\n          ><strong\n            ><span style=\"color: #000000\">Thời Lượng Pin Ấn Tượng</span></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          ><strong>Redmi Buds 6 Active</strong> sở hữu\n          <strong>thời lượng pin lên đến 30 giờ</strong> khi sử dụng cùng hộp\n          sạc. Chỉ với một lần sạc đầy, bạn có thể thoải mái nghe nhạc liên tục\n          trong 6 giờ đồng hồ. Hơn thế nữa, tính năng sạc nhanh của sản phẩm cho\n          phép bạn có thêm 1 giờ nghe nhạc chỉ sau 10 phút sạc. Điều này giúp\n          bạn không phải lo lắng về việc hết pin giữa chừng trong suốt ngày dài\n          bận rộn.</span\n        >\n      </p>\n      \n      <h3>\n        <span id=\"Cong_Nghe_Bluetooth_54_Tien_Tien\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Công Nghệ Bluetooth 5.4 Tiên Tiến</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          >Sản phẩm sử dụng công nghệ <strong>Bluetooth 5.4</strong> mới nhất,\n          giúp kết nối ổn định và nhanh chóng. Độ trễ siêu thấp chỉ 90ms đảm bảo\n          trải nghiệm chơi game và xem phim mượt mà, không bị gián đoạn. Đồng\n          thời, khả năng kết nối nhanh giúp bạn dễ dàng ghép đôi tai nghe với\n          các thiết bị di động chỉ trong vài giây. Đây là một tính năng rất hữu\n          ích, đặc biệt đối với những người dùng thường xuyên di chuyển hoặc cần\n          kết nối nhanh chóng trong công việc hàng ngày.</span\n        >\n      </p>\n      <h3>\n        <span id=\"Tich_Hop_Tro_Ly_Giong_Noi_Thong_Minh\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Tích Hợp Trợ Lý Giọng Nói Thông Minh</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          ><strong>Redmi Buds 6 Active</strong> còn được tích hợp\n          <strong>trợ lý giọng nói</strong>, giúp bạn dễ dàng thực hiện các thao\n          tác chỉ với một lệnh đơn giản. Chỉ cần chạm và giữ tai nghe trong 2\n          giây, trợ lý giọng nói sẽ được kích hoạt, hỗ trợ bạn trong việc gọi\n          điện, gửi tin nhắn, hoặc điều khiển phát nhạc mà không cần sử dụng\n          điện thoại. Tính năng này không chỉ mang lại sự tiện lợi mà còn giúp\n          tăng cường hiệu suất làm việc, đặc biệt là khi bạn đang bận rộn với\n          các công việc khác.</span\n        >\n      </p>\n      <h3>\n        <span id=\"De_Dang_Tuy_Chinh_Voi_Ung_Dung_Xiaomi_Earbuds\"\n          ><strong\n            ><span style=\"color: #000000\"\n              >Dễ Dàng Tùy Chỉnh Với Ứng Dụng Xiaomi Earbuds</span\n            ></strong\n          ></span\n        >\n      </h3>\n      <p>\n        <span style=\"color: #000000\"\n          >Người dùng có thể dễ dàng tùy chỉnh các cài đặt âm thanh của\n          <strong>Redmi Buds 6 Active</strong> thông qua ứng dụng\n          <strong>Xiaomi Earbuds</strong>. Ứng dụng này cung cấp nhiều tùy chọn\n          tùy chỉnh, giúp bạn điều chỉnh âm lượng, chế độ EQ, và các bản nâng\n          cấp phần mềm một cách dễ dàng. Bạn cũng có thể kiểm tra tình trạng pin\n          của tai nghe ngay trên ứng dụng, giúp quản lý thời lượng sử dụng hiệu\n          quả hơn.</span\n        >\n      </p>\n      <p>\n        <span style=\"color: #000000\"\n          ><strong>Redmi Buds 6 Active</strong> không chỉ đơn thuần là một sản\n          phẩm tai nghe không dây mà còn là một công cụ giúp nâng cao trải\n          nghiệm âm thanh của bạn trong mọi hoạt động hàng ngày. Với thiết kế\n          hiện đại, tính năng giảm tiếng ồn tiên tiến, thời lượng pin ấn tượng,\n          và chất lượng âm thanh đỉnh cao, sản phẩm này chắc chắn sẽ làm hài\n          lòng ngay cả những người dùng khó tính nhất. Dù bạn là người yêu thích\n          âm nhạc, một game thủ, hay một người thường xuyên di chuyển,\n          <strong>Redmi Buds 6 Active</strong> sẽ là lựa chọn hoàn hảo để đồng\n          hành cùng bạn trong mọi tình huống. Đây chính là sản phẩm không thể\n          thiếu đối với những ai yêu cầu sự kết hợp giữa công nghệ tiên tiến và\n          tính thẩm mỹ cao trong một chiếc tai nghe không dây.</span\n        >\n      </p>\n    </div>\n  </div>\n</div>', 1748351138, 1748351138),
(4, 1, 'Xiaomi Type-C Earphones', '169000', 23, 'Xiaomi_Type-C_Earphones.png', 10, 152100, 11, 4, 1, '<ul>\r\n    <li>Thiết kế gọn nhẹ, thoải mái khi đeo trong thời gian dài</li>\r\n    <li>Đầu kết nối Type-C tương thích với nhiều thiết bị hiện đại</li>\r\n    <li>Chất lượng âm thanh rõ ràng, đáp ứng tốt nhu cầu nghe nhạc và gọi điện</li>\r\n    <li>Micro tích hợp giúp đàm thoại dễ dàng, lọc tạp âm cơ bản</li>\r\n    <li>Dây cáp bền bỉ, chống rối hiệu quả</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\r\n  <div class=\"ksp-content p-2 mb-2\">\r\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\r\n    <div>\r\n      <ul>\r\n        <li>\r\n          Driver 12.4mm cho âm thanh mạnh mẽ, tái tạo âm trầm sâu lắng, âm cao\r\n          trong trẻo\r\n        </li>\r\n        <li>\r\n          Thiết kế dạng in-ear ôm sát tai, cách âm tốt, mang đến không gian âm\r\n          nhạc riêng tư\r\n        </li>\r\n        <li>\r\n          Sử dụng nhanh chóng với đầu kết nối Type-C tương thích với nhiều thiết\r\n          bị Android\r\n        </li>\r\n        <li>\r\n          Kháng nước IP54 thoải mái sử dụng khi tập luyện thể thao, không lo mồ\r\n          hôi hay mưa\r\n        </li>\r\n      </ul>\r\n    </div>\r\n  </div>\r\n  <div id=\"cpsContentSEO\">\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C đi kèm hệ thống driver 12.4mm với âm thanh\r\n      tái tạo chất lượng cao, kết hợp khả năng kết nối đầu Type-C ổn định, dễ sử\r\n      dụng. Đặc biệt, mẫu\r\n      <strong\r\n        >\r\n          tai nghe có dây\r\n        </strong\r\n      >\r\n      của Xiaomi này còn đạt chuẩn IP54 kháng nước vượt trội. Cộng thêm thiết kế\r\n      in-ear vừa vặn, cho người dùng thoải mái trải nghiệm trong không gian âm\r\n      nhạc riêng tư.&nbsp;\r\n    </p>\r\n    \r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"tai-nghe-co-day-xiaomi-type-c--am-thanh-sau-lang-trai-nghiem-toi-uu\"\r\n    >\r\n      <strong>\r\n        Tai nghe có dây Xiaomi Type-C – Âm thanh sâu lắng, trải nghiệm tối ưu\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C là thiết bị âm thanh sở hữu nhiều đặc điểm\r\n      ưu trội để người dùng thư giãn cùng những bản nhạc một cách hoàn hảo. Với\r\n      chất âm chuẩn chỉnh, thiết kế hiện đại, chiếc tai nghe có dây này hứa hẹn\r\n      sẽ đáp ứng mọi nhu cầu âm nhạc của nhiều đối tượng.\r\n    </p>\r\n    <h3 style=\"scroll-margin: 154px\" id=\"cong-suat-phu-hop-cong-nghe-hoan-hao\">\r\n      <strong> Công suất phù hợp, công nghệ hoàn hảo </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C trang nghệ hệ thống Dynamic Driver kích\r\n      thước 12.4mm cho phép kiến tạo âm thanh chân thực, sống động. Từ dải bass\r\n      trầm lắng đến âm treble trong trẻo, chiếc tai nghe này đều dễ dàng thể\r\n      hiện chính xác theo từng giai điệu. Đồng thời, phiên bản cũng có thể điều\r\n      chỉnh và phân tích âm thanh nhanh chóng, chuyên nghiệp.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/tai-nghe-co-day-xiaomi-type-c-1.jpg\"\r\n        alt=\"Công suất của tai nghe có dây Xiaomi Type-C\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Với công suất vừa đủ, thiết bị có thể chinh phục mọi thể loại nhạc với\r\n      chất âm tự nhiên, rõ ràng. Tai nghe cung cấp âm lượng đủ lớn với dải tần\r\n      số rộng mà không gây méo tiếng, cho phép tận hưởng âm nhạc ở bất cứ đâu,\r\n      bất cứ khi nào. Cộng thêm công nghệ tiên tiến, phiên bản không chỉ đảm bảo\r\n      chất lượng âm thanh vượt trội mà còn tập trung vào không gian trải nghiệm\r\n      âm nhạc riêng tư của người dùng.&nbsp;\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thiet-ke-thoai-mai-kieu-dang-thich-hop\"\r\n    >\r\n      <strong> Thiết kế thoải mái, kiểu dáng thích hợp </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C chú trọng vào sự thoải mái của người nghe\r\n      với thiết kế in-ear ôm sát, tương thích nhiều kích cỡ tai để giữ cảm giác\r\n      dễ chịu khi đeo lâu. Phần earcup được làm từ chất liệu cao cấp, êm ái,\r\n      giúp ngăn chặn âm thanh rò rỉ ra ngoài, cũng như tránh tình huống rơi rớt\r\n      trong quá trình hoạt động.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/tai-nghe-co-day-xiaomi-type-c-2.jpg\"\r\n        alt=\"Thiết kế tai nghe có dây Xiaomi Type-C\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Kết hợp với kiểu dáng năng động, chiếc tai nghe có dây này mang đến thẩm\r\n      mỹ, cũng như phù hợp đa dạng sở thích mỗi người. Bên cạnh đó, màu sắc của\r\n      thiết bị cũng hợp xu hướng hiện nay, giúp người dùng tự tin lựa chọn theo\r\n      phong cách.\r\n    </p>\r\n    <h3 style=\"scroll-margin: 154px\" id=\"micro-chuyen-dung-chong-nuoc-ip54\">\r\n      <strong> Micro chuyên dụng, chống nước IP54 </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C tích hợp micro trên dây để hỗ trợ người dùng\r\n      thực hiện cuộc gọi hay thu âm một cách hiệu quả. Kể cả trong môi trường\r\n      nhiều tạp âm, hệ thống vẫn bắt âm tốt nhằm mang đến bản âm chất lượng, rõ\r\n      ràng. Từ đó, người dùng có thể trực tiếp trò chuyện khi đeo tai nghe.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/tai-nghe-co-day-xiaomi-type-c-3.jpg\"\r\n        alt=\"Mic đàm thoải, khả năng chống nước của tai nghe có dây Xiaomi Type-C\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Mặt khác, chiếc tai nghe này còn nổi bật với tính năng kháng nước IP54, đủ\r\n      để giúp người dùng sử dụng hiệu quả ngay cả khi đeo dưới trời mưa, hay tập\r\n      thể dục thể thao. Đồng thời, hạn chế ảnh hưởng đến quá trình xử lý và\r\n      truyền phát âm thanh nếu như vô tình tiếp xúc nhẹ với nước.\r\n    </p>\r\n    <h3 style=\"scroll-margin: 154px\" id=\"ket-noi-on-dieu-khien-don-gian\">\r\n      <strong> Kết nối ổn, điều khiển đơn giản </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe có dây Xiaomi Type-C tương thích tốt với các thiết bị có đầu\r\n      Type-C. Qua kết nối nhanh, ổn định qua dây cáp bền bỉ, thiết bị cho phép\r\n      truyền âm thanh đến người nghe với hiệu suất cao, không gian đoạn. Nhờ vậy\r\n      giúp bảo đảm trải nghiệm nghe nhạc mượt mà, liền mạch trong nhiều giờ liên\r\n      tục. Thêm vào đó, ba nút điều khiển trên dây còn được bố trí hợp lý để\r\n      tăng giảm hoặc chuyển bài thuận tiện cho người dùng.&nbsp;\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/tai-nghe-co-day-xiaomi-type-c-4.jpg\"\r\n        alt=\"Kết nối, điều khiển trên tai nghe có dây Xiaomi Type-C\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    \r\n  </div>\r\n</div>\r\n', 1748351279, 1748351279);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `id_danhmuc`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `giamgia`, `giadagiam`, `daban`, `averange_rating`, `trangthaisp`, `tomtat`, `noidung`, `created_time`, `last_updated`) VALUES
(5, 1, 'Redmi Buds 6 Pro', '1990000', 40, 'Redmi_Buds_6_Pro.png', 20, 1592000, 9, 5, 1, '<ul>\n    <li> Thiết kế in-ear hiện đại, gọn nhẹ – đeo êm ái suốt nhiều giờ mà không bị đau tai</li>\n    <li> Driver động 11mm kết hợp loa tweeter 10mm – tái tạo âm thanh chi tiết, sống động</li>\n    <li> Chống ồn chủ động (ANC) lên đến 52dB – loại bỏ tạp âm hiệu quả, tận hưởng âm nhạc trọn vẹn</li>\n    <li> AI khử ồn khi gọi thoại – giọng nói rõ ràng ngay cả trong môi trường đông người</li>\n    <li> Thời lượng pin lên đến 38 giờ với hộp sạc – thoải mái nghe nhạc cả tuần</li>\n</ul>\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\r\n  <div class=\"ksp-content p-2 mb-2\">\r\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\r\n    <div>\r\n      <ul>\r\n        <li>\r\n          Chống ồn chủ động 55dB, khả năng chống ồn tốt hơn thế hệ trước đến\r\n          14,16% mang lại trải nghiệm âm thanh hoàn hảo.\r\n        </li>\r\n        <li>\r\n          Redmi Buds 6 Pro có thể xác định và theo dõi vị trí đầu của khách hàng\r\n          một cách chính xác thông qua cảm biến chuyển động, mang lại không gian\r\n          âm thanh 3D đỉnh cao.\r\n        </li>\r\n        <li>\r\n          Công nghệ âm thanh đạt chuẩn Hi-Res Audio và hỗ trợ codec âm thanh\r\n          LDAC mang lại trải nghiệm sắc âm tuyệt hảo.\r\n        </li>\r\n      </ul>\r\n    </div>\r\n  </div>\r\n  <div id=\"cpsContentSEO\">\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe không dây Xiaomi Redmi Buds 6 Pro trang bị thiết kế dạng in-ear\r\n      nhằm đảm bảo trải nghiệm đeo chắc chắn khi dùng trong các hoạt động thể\r\n      chất. Mẫu tai nghe không dây\r\n      này được trang bị 3 micro AI với khả năng lọc gió lên tới 12m/s. Tai nghe\r\n      Redmi Buds 6 Pro mang đến thời lượng pin lên đến 9.5 giờ mỗi lần sử dụng\r\n      và có tổng thời lượng nghe tối đa 36 giờ.\r\n    </p>\r\n    \r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"vi-sao-nen-mua-tai-nghe-xiaomi-redmi-buds-6-pro\"\r\n    >\r\n      <strong>Vì sao nên mua tai nghe&nbsp;Xiaomi Redmi Buds 6 Pro?</strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Xiaomi Redmi Buds 6 Pro không chỉ sở hữu một vẻ ngoài tối ưu mà còn được\r\n      trang bị công nghệ cùng tính năng tối ưu các trải nghiêm nghe, hứa hẹn là\r\n      một sản phẩm đáng trải nghiệm. Điểm nhanh một vài điểm nổi bật\r\n      trên&nbsp;Redmi Buds 6 Pro phải kể đến như:\r\n    </p>\r\n    <ul>\r\n      <li>\r\n        <p>\r\n          Âm thanh không gian 350 độ: Thoải mái đắm chìm trong các giai điệu\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>3 Micro chống ồn AI: Đàm thoại chất lượng, giọng nói rõ nét</p>\r\n      </li>\r\n      <li><p>Pin bền bỉ có thể đến 36 giờ: Sử dụng thoải mái cả ngày</p></li>\r\n    </ul>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"so-sanh-chi-tiet-tai-nghe-xiaomi-redmi-buds-6-pro-voi-xiaomi-redmi-buds-5-pro\"\r\n    >\r\n      <strong\r\n        >So sánh chi tiết tai nghe&nbsp;Xiaomi Redmi Buds 6 Pro với&nbsp;Xiaomi\r\n        Redmi Buds 5 Pro</strong\r\n      >\r\n    </h2>\r\n    <table border=\"0\">\r\n      <tbody>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p><strong>Tiêu chí</strong></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p><strong>Xiaomi Redmi Buds 6 Pro</strong></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p><strong>Xiaomi Redmi Buds 5 Pro</strong></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Màu sắc</p></td>\r\n          <td style=\"text-align: center\"><p>Xanh - Trắng - Đen</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>Đen bán dạ -&nbsp;Trắng ánh trăng -&nbsp;Tím cực quang</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Trọng lượng</p></td>\r\n          <td style=\"text-align: center\"><p>36g (Tai nghe và hộp sạc)</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>5,1g (Tai nghe)</p>\r\n            <p>42,8g (Tai nghe và hộp sạc)</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Drive</p></td>\r\n          <td style=\"text-align: center\"><p>11mm</p></td>\r\n          <td style=\"text-align: center\"><p>11mm</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Công nghệ âm thanh</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>Âm thanh 360 độ</p>\r\n            <p>Hi-res Audio</p>\r\n          </td>\r\n          <td style=\"text-align: center\"><p>Âm thanh Hi-Res</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Pin</p></td>\r\n          <td style=\"text-align: center\"><p>Sử dụng đến 36 giờ</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>54mAh (Tai nghe)</p>\r\n            <p>480mAh (Hộp sạc)</p>\r\n            <p>Sử dụng đến 38 giờ</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Kết nối&nbsp;</p></td>\r\n          <td style=\"text-align: center\"><p>Bluetooth 5.3</p></td>\r\n          <td style=\"text-align: center\"><p>Bluetooth® 5.3</p></td>\r\n        </tr>\r\n      </tbody>\r\n    </table>\r\n    <h2\r\n      id=\"tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-thiet-ke-nhe-va-mau-sac-hien-dai-tinh-te\"\r\n      style=\"scroll-margin: 154px\"\r\n    >\r\n      <strong\r\n        >Tai nghe không dây Xiaomi Redmi Buds 6 Pro - Thiết kế nhẹ và màu sắc\r\n        hiện đại, tinh tế</strong\r\n      >\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe không dây Xiaomi Redmi Buds 6 Pro sẽ đáp ứng nhu cầu nghe nhạc và\r\n      gọi điện không dây tiện lợi cho người dùng. Với thiết kế tinh tế và dễ sử\r\n      dụng, Redmi Buds 6 Pro mang đến sự thoải mái khi đeo trong thời gian dài.\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thiet-ke-nhe-va-mau-sac-hien-dai-tinh-te\"\r\n    >\r\n      <strong>Thiết kế nhẹ và màu sắc hiện đại, tinh tế</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Với thiết kế tinh tế dạng in-ear cùng trọng lượng nhẹ, Xiaomi Redmi Buds 6\r\n      Pro sẽ đem lại sự thoải mái và không gây cảm giác khó chịu khi đeo lâu.\r\n      Thiết kế hình cung giúp cân bằng giữa trong và ngoài tai, đảm bảo tai nghe\r\n      nằm chắc chắn nhưng không tạo áp lực khi nghe.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-3_1.jpg\"\r\n        alt=\"Thiết kế tai nghe không dây Xiaomi Redmi Buds 6 Pro\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe không dây Xiaomi Redmi Buds 6 Pro có ba màu là xanh mint, đen và\r\n      trắng, thể hiện sự trẻ trung và sinh động. Hộp sạc với bốn góc bo tròn tạo\r\n      nên dáng vẻ thanh mảnh, nhỏ gọn, dễ cầm tay và dễ dàng mang theo. Thiết kế\r\n      thanh lịch này không chỉ giúp bạn luôn có tai nghe bên mình mà còn mang\r\n      đến một phong cách thời trang tinh tế, phù hợp với người dùng yêu thích sự\r\n      tiện dụng và thẩm mỹ.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-1_1.jpg\"\r\n        alt=\"Thiết kế nhẹ và màu sắc hiện đại, tinh tế\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h3 style=\"scroll-margin: 154px\" id=\"cong-nghe-giam-tieng-on-tich-hop-ai\">\r\n      <strong>Công nghệ giảm tiếng ồn tích hợp AI</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe được trang bị 3 micro AI có khả năng lọc gió tới 12m/s giúp giảm\r\n      tiếng ồn hiệu quả và mang đến giọng nói rõ ràng trong mọi cuộc gọi, ngay\r\n      cả trong môi trường ồn ào. Giờ đây người dùng có thể thoải mái trò chuyện\r\n      với bạn bè và hay người dùng rõ ràng trong qua trình di chuyển, luyện tập\r\n      mà không lo vấn đề bị ngắt quãng. Sản phẩm còn có độ nhạy âm thanh lên tới\r\n      16.000 lần để giảm tiếng ồn tự động theo thời gian thực.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-2_1.jpg\"\r\n        alt=\"Mic đàm thoại của tai nghe không dây Xiaomi Redmi Buds 6 Pro\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thoi-luong-dung-den-36-gio-ho-tro-sac-nhanh\"\r\n    >\r\n      <strong>Thời lượng dùng đến 36 giờ, hỗ trợ sạc nhanh</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe mang đến thời lượng pin lên đến 9.5 giờ sau một lần sạc, và tổng\r\n      cộng có thể kéo dài tối đa 36 giờ khi kết hợp với hộp sạc. Nhờ vậy, người\r\n      dùng không cần lo lắng về việc pin cạn kiệt, cho phép tận hưởng âm nhạc\r\n      liền mạch suốt cả ngày.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-4.jpg\"\r\n        alt=\"Thời lượng pin của tai nghe không dây Xiaomi Redmi Buds 6 Pro\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Hơn nữa, với khả năng sạc nhanh 5 phút để có thêm 2 giờ nghe nhạc, tai\r\n      nghe đáp ứng nhu cầu sử dụng ngay lập tức. Bạn sẽ an tâm sử dụng để trải\r\n      nghiệm tai nghe không dây Xiaomi Redmi Buds 6 Pro nhiều giờ sau đó sạc và\r\n      sử dụng trong thời gian nhanh chóng.\r\n    </p>\r\n    <h3 style=\"scroll-margin: 154px\" id=\"dam-chim-trong-giai-dieu-am-nhac\">\r\n      <strong>Đắm chìm trong giai điệu âm nhạc</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe Redmi Buds 6 Pro là mẫu tai nghe không dây đầu tiên của Xiaomi\r\n      được trang bị bộ ba khối đồng trục bằng gốm. Với driver 11mm phủ màng chắn\r\n      titan và bộ đôi áp điện bằng gốm 6.7mm, các dải âm trầm, trung và bổng sẽ\r\n      được xử lý chi tiết hơn. Nhờ công nghệ này mà âm lượng tần số cao trên tai\r\n      nghe còn được tăng lên 10% so với thế hệ tiền nhiệm để âm thanh từ nhạc cụ\r\n      được tái tạo tốt, rõ ràng hơn.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Buds/Tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-1.jpg\"\r\n        alt=\"Đắm chìm trong giai điệu âm nhạc\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe không dây Xiaomi Redmi Buds 6 Pro còn hỗ trợ các bộ giải mã MIHC,\r\n      LHDC 5.0 và LC3 để âm thanh có độ phân giải cao, loại bỏ hoàn toàn hiện\r\n      tượng méo tiếng. Trong đó, bộ giải mã MIHC là codec lần đầu tiên xuất\r\n      hiện, do chính Xiaomi tự phát triển. Điều này giúp cho người dùng luôn có\r\n      thể trải nghiệm các bản nhạc với chất lượng cao theo chuẩn Hi-Res Audio\r\n      Wireless.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Để tối ưu trải nghiệm nghe, tai nghe Redmi Buds 6 Pro còn được tích hợp\r\n      tính năng theo dõi chuyển động đầu độc đáo. Bên trong tai nghe là cảm biến\r\n      con quay hồi chuyển sáu trục, có chức năng theo dõi các chuyển động đầu.\r\n      Cảm biến này sẽ cộng hưởng theo hơi thở và chuyển động của người dùng để\r\n      tạo nên sự hoà hợp với giai điệu bản nhạc.\r\n    </p>\r\n    <h3\r\n      id=\"ket-noi-thiet-bi-chia-se-am-thanh-tien-loi\"\r\n      style=\"scroll-margin: 154px\"\r\n    >\r\n      <strong>Kết nối thiết bị, chia sẻ âm thanh tiện lợi</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe Redmi Buds 6 Pro được Xiaomi trang bị công nghệ Bluetooth 5.3 và\r\n      các giao thức kết nối Bluetooth Low Energy, HFP, A2DP, AVRCP. Điều này\r\n      giúp mẫu tai nghe không dây Redmi Buds 6 Pro không chỉ kết nối tốt với mọi\r\n      dòng điện thoại, laptop, tablet,...được phát triển bởi bên thứ ba trong\r\n      phạm vi 10m mà còn tối ưu trải nghiệm trên các thiết bị của Xiaomi.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Buds/Tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-2.jpg\"\r\n        alt=\"Kết nối thiết bị, chia sẻ âm thanh tiện lợi\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe không dây Xiaomi Redmi Buds 6 Pro có khả năng chuyển đổi thông\r\n      minh giữa các thiết bị cài đặt hệ điều hành HyperOS. Ngoài ra, hai tai\r\n      nghe Redmi Buds 6 Pro còn dễ dàng kết nối với cùng một thiết bị. Điều này\r\n      giúp tăng trải nghiệm giải trí khi người dùng có thể tiện lợi chia sẻ trực\r\n      tiếp các nội dung, âm thanh qua chiếc tai nghe thứ hai.\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"mua-tai-nghe-khong-day-xiaomi-redmi-buds-6-pro-uy-tin-tai-cellphones\"\r\n    >\r\n      <strong\r\n        >Mua tai nghe không dây Xiaomi Redmi Buds 6 Pro uy tín tại\r\n        CellphoneS</strong\r\n      >\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Mua tai nghe không dây Xiaomi Redmi Buds 6 Pro với giá tốt và uy tín tại\r\n      cửa hàng của CellphoneS. Khi mua hàng tại đây, khách hàng có thể hoàn toàn\r\n      yên tâm về sản phẩm nhờ ưu đãi ấn tượng và bảo hành sau khi\r\n      mua.&nbsp;Ngoài ra, hệ thống CellphoneS có nhân viên sẵn sàng giải đáp câu\r\n      hỏi và tận tình mợ tư vấn khách hàng khi cần. Bạn có thể đặt hàng qua\r\n      website một cách đơn giản để không cần phải đến tận nơi mua như trước đây.\r\n    </p>\r\n  </div>\r\n  <div class=\"cps-block-content_btn-showmore\" style=\"display: none\">\r\n    <a class=\"btn-show-more button__content-show-more\">\r\n      Xem thêm  \r\n      <div>\r\n        <svg\r\n          xmlns=\"http://www.w3.org/2000/svg\"\r\n          viewBox=\"0 0 448 512\"\r\n          width=\"10\"\r\n          height=\"10\"\r\n        >\r\n          <path\r\n            d=\"M224 416c-8.188 0-16.38-3.125-22.62-9.375l-192-192c-12.5-12.5-12.5-32.75 0-45.25s32.75-12.5 45.25 0L224 338.8l169.4-169.4c12.5-12.5 32.75-12.5 45.25 0s12.5 32.75 0 45.25l-192 192C240.4 412.9 232.2 416 224 416z\"\r\n          ></path>\r\n        </svg></div\r\n    ></a>\r\n  </div>\r\n</div>\r\n', 1748351402, 1748351402),
(6, 1, 'Xiaomi Buds 5', '1990000', 10, 'Xiaomi_Buds_5.png', 10, 1791000, 8, 3, 1, '<ul>\n    <li> Thiết kế công thái học mới – đeo êm, nhẹ, bám tai chắc chắn suốt ngày dài</li>\n    <li> Âm thanh chất lượng cao với driver 11mm – mang đến trải nghiệm nghe sống động và mạnh mẽ</li>\n    <li> Chống ồn chủ động (ANC) lên đến 46dB – loại bỏ tạp âm, đắm chìm trong thế giới âm nhạc</li>\n    <li> AI khử tiếng ồn cuộc gọi – đàm thoại rõ ràng ngay cả trong môi trường ồn ào</li>\n    <li> Pin tới 38 giờ sử dụng cùng hộp sạc – nghe nhạc cả tuần không lo gián đoạn</li>\n</ul>\n', '<div class=\"rm-detail\">\r\n  <div id=\"gioithieu\">\r\n    <div class=\"ex_cntitle ex_cnpost\">Đánh giá chi tiết</div>\r\n    <div class=\"product-description dtshow\" style=\"height: 400px\">\r\n      <div\r\n        class=\"lwptoc lwptoc-baseItems lwptoc-light lwptoc-notInherit\"\r\n        data-smooth-scroll=\"1\"\r\n        data-smooth-scroll-offset=\"24\"\r\n        data-lwptoc-initialized=\"1\"\r\n      >\r\n        \r\n      </div>\r\n      <h3>\r\n        <span id=\"Redmi_Buds_5_voi_thiet_ke_tre_trung_kich_thuoc_nho_gon\"\r\n          ><strong\r\n            >Redmi Buds 5​ với thiết kế trẻ trung, kích thước nhỏ gọn</strong\r\n          ></span\r\n        >\r\n      </h3>\r\n      <p>\r\n        Đầu tiến là về phần hộp đựng, cũng như những thế hệ trước thì phần hộp\r\n        đựng bên ngoài của Redmi Buds 5 được làm rất nhỏ gọn với tông màu trắng\r\n        chủ đạo cùng hình ảnh sản phẩm được in nổi bật ngay chính diện. Cách làm\r\n        này tuy đơn giản nhưng những điểm quan trọng như hình ảnh mô tả, tên sản\r\n        phẩm hay các điểm nổi bật của thiết bị đều được mô tả để người dùng dễ\r\n        dàng tiếp cận hơn.\r\n      </p>\r\n      <div class=\"imgwrap\">\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"lazyloaded aligncenter wptangtoc-lazy entered gtloader\"\r\n          title=\"Trên tay Redmi Buds 5\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lhx2bj31kw11xdlp-800-resize.jpg\"\r\n          alt=\"Trên tay Redmi Buds 5\"\r\n          data-original=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lhx2bj31kw11xdlp-800-resize.jpg\"\r\n          width=\"800\"\r\n          height=\"533\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lhx2bj31kw11xdlp-800-resize.jpg\"\r\n          data-ll-status=\"loaded\"\r\n        />\r\n      </div>\r\n      <div class=\"captionnews\">\r\n        Hộp đựng bên ngoài của Redmi Buds 5 có tông màu trắng chủ đạo\r\n      </div>\r\n      <p>\r\n        Bên trong phần hộp thì Redmi cũng đã trang bị thêm cho người dùng những\r\n        phụ kiện đi kèm như: Sách hướng dẫn sử dụng, 3 cặp nút tai cho bạn dễ\r\n        dàng thay thế và cáp sạc USB Type-C. Các trang bị này không quá nhiều\r\n        nhưng đủ để bạn có thể sử dụng thiết bị một cách thuận lợi ngay sau khi\r\n        mở hộp.\r\n      </p>\r\n      <div class=\"imgwrap\">\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"lazyloaded aligncenter wptangtoc-lazy entered gtloader\"\r\n          title=\"Trên tay Redmi Buds 5\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lmm1kj31kw11x7dz-800-resize.jpg\"\r\n          alt=\"Trên tay Redmi Buds 5\"\r\n          data-original=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lmm1kj31kw11x7dz-800-resize.jpg\"\r\n          width=\"800\"\r\n          height=\"533\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5lmm1kj31kw11x7dz-800-resize.jpg\"\r\n          data-ll-status=\"loaded\"\r\n        />\r\n      </div>\r\n      <p>\r\n        Ở Redmi Buds 5 chúng ta sẽ thấy thiết bị có một ngoại hình vuông vức và\r\n        trẻ trung với thiết kế hộp sạc theo dạng hình chữ nhật. So với thế hệ\r\n        tiền nhiệm dùng hình oval thì mình lại ấn tượng với phiên bản Redmi Buds\r\n        5 hơn. Ngoài ra, các cạnh góc của phần hộp sạc đều được bo cong mềm mại\r\n        trông vô cùng hài hòa và được làm trơn màu rất đẹp mắt.\r\n      </p>\r\n      <div class=\"imgwrap\">\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"lazyloaded aligncenter wptangtoc-lazy entered gtloader\"\r\n          title=\"Trên tay Redmi Buds 5\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5llkipj31kw11xjxg-800-resize.jpg\"\r\n          alt=\"Trên tay Redmi Buds 5\"\r\n          data-original=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5llkipj31kw11xjxg-800-resize.jpg\"\r\n          width=\"800\"\r\n          height=\"533\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2023/10/006ruxyjly1hi4i5llkipj31kw11xjxg-800-resize.jpg\"\r\n          data-ll-status=\"loaded\"\r\n        />\r\n      </div>\r\n      <div class=\"captionnews\">\r\n        Tổng thể bên ngoài của Redmi Buds 5 được làm tối giản với hộp sạc hình\r\n        chữ nhật phủ nhám\r\n      </div>\r\n      <p>\r\n        Toàn bộ phần hộp sạc của Redmi Buds 5 được hoàn thiện từ nhựa cứng phủ\r\n        nhám, giúp hạn chế tình trạng bám vân tay và mồ hôi để đảm bảo vẻ ngoài\r\n        luôn được đẹp mắt. Mặt trước của phần hộp được in logo ‘Redmi’ phủ bóng\r\n        giúp nổi bật hơn trên nền màu trơn của thiết bị. Ở&nbsp;Redmi Buds 5,\r\n        hãng cũng trang bị 3 phiên bản màu khác nhau cho thiết bị là: Đen, Trắng\r\n        và Tím, mỗi màu đều có một vẻ đẹp riêng và khi kết hợp với thiết kế tổng\r\n        thể của&nbsp;Redmi Buds 5 thì lại tôn lên được sự tối giản và trẻ trung\r\n        rất khéo.\r\n      </p>\r\n      <div class=\"imgwrap\">\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"lazyloaded aligncenter wptangtoc-lazy entered gtloader\"\r\n          title=\"Trên tay Redmi Buds 5\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2023/10/82d30882gy1hi4hf3b85aj21jk114k4c-800-resize.jpg\"\r\n          alt=\"Trên tay Redmi Buds 5\"\r\n          data-original=\"https://mihome.vn/wp-content/uploads/2023/10/82d30882gy1hi4hf3b85aj21jk114k4c-800-resize.jpg\"\r\n          width=\"800\"\r\n          height=\"450\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2023/10/82d30882gy1hi4hf3b85aj21jk114k4c-800-resize.jpg\"\r\n          data-ll-status=\"loaded\"\r\n        />\r\n      </div>\r\n      <div class=\"captionnews\">\r\n        Redmi Buds 5 có ba phiên bản màu khác nhau là Đen, Trắng và Tím\r\n      </div>\r\n      <p>\r\n        Khác với phần hộp phủ nhám, phần tai nghe của Redmi Buds 5 lại được phủ\r\n        bóng nhằm tăng thêm sự nổi bật và được đặt nằm trong phần hộp được đóng\r\n        mở theo dạng gập vỏ sò. Phần tai nghe của Redmi Buds 5&nbsp;được thiết\r\n        kế theo dạng in-ear với đuôi tai không quá dài điểm thêm một đường vân\r\n        bóng rất bắt mắt. Phần housing được làm dạng hình bầu dục và sử dụng kèm\r\n        với eartips silicon nên khi đeo cho độ thoải mái cao.\r\n      </p>\r\n      \r\n      <div class=\"captionnews\">\r\n        Cặp tai nghe của Redmi Buds 5 được thiết kế dạng in-ear cùng phần thân\r\n        được làm dài\r\n      </div>\r\n      <p>\r\n        Bên cạnh đó,&nbsp;Redmi Buds 5 còn đạt chuẩn kháng nước IP54 cho phần\r\n        tai nghe (không áp dụng cho phần hộp). Chính vì thế, khi đeo&nbsp;Redmi\r\n        Buds 5 để tập luyện thể thao thì dù trong môi trường ẩm và mô hôi cũng\r\n        sẽ không lo hư hỏng nhưng theo khuyến cáo thì vẫn nên tránh làm rơi\r\n        thiết bị xuống nước.\r\n      </p>\r\n      <h3>\r\n        <span\r\n          id=\"Redmi_Buds_5_chat_am_hay_tinh_nang_khu_on_AI_xin_so_pin_dung_lau\"\r\n          ><strong\r\n            >Redmi Buds 5 chất âm hay, tính năng khử ồn AI xịn sò, pin dùng\r\n            lâu</strong\r\n          ></span\r\n        >\r\n      </h3>\r\n      <p>\r\n        Để kết nối được tốt và cho chất lượng âm thanh tốt, Redmi cũng đã trang\r\n        bị cho&nbsp;Redmi Buds 5 công nghệ&nbsp;Bluetooth 5.3 kết hợp với Google\r\n        Fast Pair (dành cho các thiết bị Android) và với tính những trang bị này\r\n        thì bạn chỉ cần một lần nhấn (trên điện thoại) là đã kết nối được Redmi\r\n        Buds 5 rất nhanh chóng và dễ dàng.\r\n      </p>\r\n      \r\n      <div class=\"captionnews\">\r\n        Redmi Buds 5 được kết nối thông qua công nghệ Bluetooth 5.3\r\n      </div>\r\n      <p>\r\n        Với các thiết bị tai nghe không dây nhà Redmi thì Redmi Buds 5 là phiên\r\n        bản đầu tiên sử dụng thuật toán AI kênh đôi tự phát triển, giúp hạn chế\r\n        và có thể chịu được tiếng ồn của gió lên đến 6 m/s. Chính vì thế\r\n        mà&nbsp;dù bạn đang đi xe hay chạy bộ ngoài trời thi các tạp âm xung\r\n        quanh của không ảnh hưởng được đến phần âm thanh mà&nbsp;Redmi Buds 5\r\n        cung cấp.\r\n      </p>\r\n      <div class=\"captionnews\">\r\n        Redmi Buds 5 được trang bị tính năng chống ồn và có thể loại bỏ được đến\r\n        99.5% tiếng ồn xung quanh\r\n      </div>\r\n      <p>\r\n        Chưa dừng lại ở đó, Redmi Buds 5 còn được trang bị tính năng khử tiếng\r\n        ồn lên tới 46 dB và loại bỏ được đến 99.5% tiếng ồn xung quanh. Redmi\r\n        Buds 5 có dải tần rộng 2 kHz và cung cấp 3 mức khử tiếng ồn để bạn có\r\n        thể điều chỉnh sao cho phù hợp với nhu cầu nghe của bản thân. Ở Redmi\r\n        Buds 5, hãng đã sử dụng trình điều khiển động 12.4 mm cho vùng rung âm\r\n        thanh lớn và âm trầm sâu sắc hơn. Không những thế, màng loa được phủ\r\n        titan có độ cứng tốt và cũng chính nhờ đó mà âm treble trong trẻo hơn,\r\n        giúp âm thanh cho ra rất sống động.\r\n      </p>\r\n      \r\n      <div class=\"captionnews\">\r\n        Chất âm của Redmi Buds 5 hay với trình điều khiển động 12.4 mm cùng\r\n        nhiều trang bị hữu ích\r\n      </div>\r\n      <p>\r\n        Về thời lượng pin của Redmi Buds 5, theo công bố từ nhà sản xuất\r\n        thì&nbsp;Redmi Buds 5 sở hữu thời lượng hoạt động rất ấn tượng với 10\r\n        tiếng phát nhạc và đến 40 tiếng khi dùng kèm hộp sạc ở chế độ thường\r\n        (không dùng tính năng khử tiếng ồn). Còn trong trường hợp bật tính năng\r\n        khử tiếng ồn thì&nbsp;Redmi Buds 5 có thể hoạt động đến 8 tiếng cho việc\r\n        nghe nhạc và dùng được đến 30 tiếng khi kết hợp với hộp sạc.\r\n      </p>\r\n  </div>\r\n</div>\r\n', 1748351654, 1748351654),
(7, 1, 'Redmi Buds 6 Play', '340000', 34, 'Redmi_Buds_6_Play.png', 15, 289000, 19, 3, 1, '<ul>\r\n        <li>Tai nghe hình hạt đậu gọn gàng dễ chịu</li>\r\n        <li>Thời lượng pin lên tới 36 giờ để phát nhạc cả ngày khi sử dụng hộp sạc</li>\r\n        <li>Củ loa động lớn 10 mm mang đến âm thanh mạnh mẽ</li>\r\n        <li>Giảm tiếng ồn bằng AI cho cuộc gọi rõ ràng</li>\r\n        <li>Thời lượng phát nhạc lên đến 3 giờ sau 10 phút sạc</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\r\n  <div class=\"ksp-content p-2 mb-2\">\r\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\r\n    <div>\r\n      <ul>\r\n        <li>\r\n          Công nghệ Bluetooth 5.4 mới, cho hiệu suất truyền tải nhanh chóng, kết\r\n          nối ổn định\r\n        </li>\r\n        <li>\r\n          Thiết kế công thái học cho cảm giác đeo thoải mái, hộp sạc nhỏ giúp dễ\r\n          mang theo\r\n        </li>\r\n        <li>\r\n          Công nghệ AI khử tiếng ồn và chống ồn đàm thoại ENC giúp hạn chế bị\r\n          gián đoạn\r\n        </li>\r\n        <li>\r\n          Thời lượng pin đến 36 giờ khi sử dụng cùng hộp sạc, đáp ứng tốt nhu\r\n          cầu của bạn\r\n        </li>\r\n      </ul>\r\n    </div>\r\n  </div>\r\n  <div id=\"cpsContentSEO\">\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe Bluetooth Xiaomi Redmi Buds 6 Play là dòng sản phẩm với phong\r\n      cách thiết kế dạng semi in-ear cùng driver lớn <strong> 14.2mm</strong>.\r\n      Về khả năng đàm thoại tai nghe có <strong> micro kép </strong> để khử gió\r\n      cũng như đạt độ trễ chỉ <strong> 90ms </strong> nhờ công nghệ\r\n      <strong> bluetooth 5.4</strong>. Ngoài ra bạn cũng có thể yên tâm khi tai\r\n      nghe có thể sử dụng liên tục <strong> 6 giờ và 30 giờ </strong> khi đi kèm\r\n      hộp sạc.\r\n    </p>\r\n    \r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"tai-nghe-bluetooth-xiaomi-redmi-buds-6-play-trai-nghiem-nghe-nhac-an-tuong\"\r\n    >\r\n      <strong>\r\n        Tai nghe Bluetooth Xiaomi Redmi Buds 6 Play - Trải nghiệm nghe nhạc ấn\r\n        tượng\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Không chỉ gây ấn tượng bởi mức giá rẻ Xiaomi Redmi Buds 6 Play còn mang\r\n      đến chất lượng âm thanh tốt cũng như thời lượng pin dài và sẽ là lựa chọn\r\n      tốt nhất để trải nghiệm âm nhạc. Lưu ý thông tin về Redmi Buds 6 Play bên\r\n      dưới được đánh giá dựa trên các tin đồn, các thông số có thể thay đổi khi\r\n      sản phẩm ra mắt.\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"driver-lon-14-2mm-cung-thiet-ke-in-ear-toi-uu\"\r\n    >\r\n      <strong> Driver lớn 14.2mm cùng thiết kế in-ear tối ưu </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Kích thước Driver lớn 14.2mm mang đến khả năng tái hiện mọi nốt nhạc cũng\r\n      như giọng hát được rõ ràng, mạnh mẽ hơn. Ngoài ra phần cứng của tai nghe\r\n      Bluetooth Xiaomi Redmi Buds 6 Play còn đạt chứng nhận Cloud Audio nhờ vào\r\n      khả năng tùy chỉnh đa dạng cũng như tạo ra hiệu ứng âm thanh tốt.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-bluetooth-xiaomi-redmi-buds-6-play-1.jpg\"\r\n        alt=\"Âm thanh tai nghe Xiaomi Redmi Buds 6 Play\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Thiết kế Xiaomi Redmi Buds 6 Play mang phong cách semi in ear không chỉ\r\n      giúp tối ưu hóa chất lượng âm thanh mà còn giúp hạn chế bị rơi trong quá\r\n      trình chơi thể thao. Người dùng có thể trực tiếp điều khiển bằng các thao\r\n      tác chạm hoặc thông qua trợ lý giọng nói. Về độ bền bỉ Buds 6 Play đạt\r\n      chuẩn kháng nước IPX4 do đó bạn hoàn toàn yên tâm khi sử dụng sản\r\n      phẩm.&nbsp;\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"khu-on-hieu-qua-nhieu-che-do-hieu-ung-am-thanh\"\r\n    >\r\n      <strong> Khử ồn hiệu quả, nhiều chế độ hiệu ứng âm thanh </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Tai nghe Bluetooth Xiaomi Redmi Buds 6 Play có thể nghe và đàm thoại tốt\r\n      kể cả những nơi đông người nhờ vào công nghệ khử ồn thông qua hệ thống\r\n      micro kép. Nhờ đó người dùng có thể đàm thoại mọi lúc mọi nơi mà vẫn đảm\r\n      bảo giọng nói trong trẻo.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-bluetooth-xiaomi-redmi-buds-6-play-2.jpg\"\r\n        alt=\"Tính năng tai nghe Xiaomi Redmi Buds 6 Play\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Một điểm cộng nữa của sản phẩm chính là ứng dụng Xiaomi Earphones, người\r\n      dùng có thể điều chỉnh nhiều chế độ âm thanh như tăng Bass, tăng treble\r\n      hay tập trung vào giọng hát… Bên cạnh đó bạn cũng có thể một số thông tin\r\n      khác như thời lượng pin hay chế độ kết nối ngay trên ứng dụng.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Người dùng còn có thể cài đặt tùy chỉnh các chế độ này một cách dễ dàng\r\n      theo sở thích cá nhân thông qua apps&nbsp;Xiaomi Earbuds. Bên cạnh cài đặt\r\n      âm thanh, ứng dụng cũng hỗ trợ tùy chỉnh cử chỉ cũng như nhiều tính năng\r\n      hữu ích khác. \r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/tai-nghe-bluetooth-xiaomi-redmi-buds-6-play-6.png\"\r\n        alt=\"Cách sử dụng Xiaomi Redmi Buds 6 Play\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thoi-luong-pin-dai-cung-ket-noi-on-dinh\"\r\n    >\r\n      <strong> Thời lượng pin dài cùng kết nối ổn định </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Theo một số thông tin tai nghe bluetooth Xiaomi Redmi Buds 6 Play rất có\r\n      thể sẽ có mức dung lượng pin lên đến 6 giờ cho một lần sạc và chỉ mất\r\n      khoảng hơn 1 giờ để sạc đầy cho tai nghe. Ngoài ra khi đi kèm với hộp sạc\r\n      người dùng còn có thể sử dụng đến 30 giờ. Nhờ vào việc sử dụng chuẩn kết\r\n      nối bluetooth 5.4 mới nhất Redmi Buds 6 Play chỉ đạt độ trễ rơi vào khoảng\r\n      90ms phù hợp cho việc chơi game cũng như xem phim.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Tai-nghe/Xiaomi/Redmi/tai-nghe-bluetooth-xiaomi-redmi-buds-6-play-3.jpg\"\r\n        alt=\"Pin tai nghe Xiaomi Redmi Buds 6 Play\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1748359487, 1748359487),
(8, 2, 'Redmi Watch 5', '2790000', 39, 'Redmi_Watch_5.png', 10, 2511000, 30, 5, 1, '<ul>\r\n    <li>Thiết kế màn hình AMOLED lớn 1.97 inch sắc nét, dễ nhìn ngoài trời</li>\r\n    <li>Thời lượng pin lên tới 20 ngày sử dụng trong chế độ tiết kiệm pin</li>\r\n    <li>Cảm biến theo dõi nhịp tim và SpO₂ chính xác 24/7</li>\r\n    <li>Hỗ trợ hơn 150 chế độ luyện tập thể thao</li>\r\n    <li>Sạc nhanh giúp sử dụng cả ngày chỉ sau 10 phút sạc</li>\r\n</ul>\r\n', '<article class=\"area_article\" style=\"\">\r\n  <h2 class=\"title_tab_h2\">\r\n    THÔNG TIN CHI TIẾT Xiaomi Redmi Watch 5 (Bluetooth) - Chính Hãng\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    <meta charset=\"utf-8\" />Đồng hồ thông minh\r\n    \r\n    Xiaomi Redmi Watch 5 nổi bật với màn hình AMOLED 2,07 inch, kết nối\r\n    Bluetooth v5.3, cảm biến SpO2 và kháng nước 5ATM. Máy chạy hệ điều hành\r\n    HyperOS, dung lượng pin cao 550mAh lên đến 24 ngày sử dụng với phiên bản\r\n    Bluetooth và 12 ngày với phiên bản eSIM.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-1.jpg?1733814529024\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\"><em>Xiaomi Redmi Watch 5</em></p>\r\n\r\n  <h2 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Cấu hình Xiaomi Redmi Watch 5</b\r\n    >\r\n  </h2>\r\n\r\n  <h3 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Thiết kế sang trọng, tinh tế</b\r\n    >\r\n  </h3>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Khi cầm Xiaomi Redmi Watch 5 trên tay, ấn tượng đầu tiên là thiết kế chỉn\r\n    chủ và sang trọng. Phần vỏ được làm từ hợp kim nhôm cao cấp, nhẹ nhưng vô\r\n    cùng chắc chắn. Kết hợp với núm xoay bằng thép không gỉ, mang đến cảm giác\r\n    rất “premium”. Đặc biệt hơn, viền màn hình chỉ có 2nm và tỷ lệ hiển thị lên\r\n    đến 82%, giúp tổng thể chiếc đồng hồ vừa hiện đại vừa thanh lịch.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-2.jpg?1733814541901\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\"><em>Nhiều tùy chọn dây đeo</em></p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Một điểm cộng nữa là thiết bị hỗ trợ tới 7 tùy chọn dây đeo tháo lắp, phù\r\n    hợp với nhiều phong cách khác nhau. Với cá nhân mình ưu tiên dây đeo từ tính\r\n    Kona Leather, vừa lịch lãm vừa thoải mái khi sử dụng.\r\n  </p>\r\n\r\n  <h3 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Màn hình cong AMOLED sống động</b\r\n    >\r\n  </h3>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Màn hình Xiaomi Redmi Watch 5 có kích thước 2,07 inch, độ phân giải\r\n    &nbsp;432 x 512 pixel, tần số quét 60Hz và độ sáng lên đến 1500 nits. Mình\r\n    trải nghiệm nhìn trên mẫu đồng hồ này không khác gì một màn hình thu nhỏ cao\r\n    cấp. Ngay cả khi dùng dưới ánh sáng mặt trời, màn hình vẫn hiển thị rõ nét,\r\n    màu sắc rực rỡ nhưng không bị lóa.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-3.jpg?1733814548874\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\"><em>Màn hình cong AMOLED</em></p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Màn hình cong nhẹ ôm sát, kết hợp với giao diện HyperOS 2 mượt mà, giúp mọi\r\n    thao tác vuốt chạm gần như liền mạch và thoải mái hơn bao giờ hết.\r\n  </p>\r\n\r\n  <h3 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Tính năng thông minh đa dụng</b\r\n    >\r\n  </h3>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Một trong những điểm mạnh của Redmi Watch 5 là sự đa năng trong những tính\r\n    năng thông minh. Với HyperOS 2, mình dễ dàng cài đặt ứng dụng bên thứ ba và\r\n    sử dụng những ứng dụng tích hợp sẵn như quản lý nhà thông minh, thông báo\r\n    tập trung và cả điều khiển xe thông minh.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-4.jpg?1733814555901\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <em>Nhiều tính năng thông minh</em>\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Đặc biệt, hỗ trợ NFC là một nâng cấp đáng giá, việc thanh toán qua đồng hồ\r\n    hoặc dùng làm chìa khóa xe thông minh vô cùng tiện lợi. Nếu bạn chọn phiên\r\n    bản eSIM, bạn có thể gọi điện, nhắn tin mà không cần mang theo điện thoại.\r\n  </p>\r\n\r\n  <h3 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Theo dõi sức khỏe toàn diện</b\r\n    >\r\n  </h3>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Trong quá trình sử dụng, mình đánh giá khá cao các tính năng theo dõi sức\r\n    khỏe trên thiết bị. Đồng hồ theo dõi nhịp tim liên tục 24/24, đo SpO2, phân\r\n    tích giấc ngủ và thậm chí quản lý căng thẳng khá chi tiết. Dữ liệu đưa ra vô\r\n    cùng chính xác, đặc biệt với nhịp tim nhờ vào con chip AFE mới.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-5.jpg?1733814563596\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\"><em>Chip đo nhịp tim mới AFE</em></p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Mình cũng đã thử nghiệm một số môn thể thao ngoài trời và tính năng định vị\r\n    GNSS hoạt động vô cùng hiệu quả. Với hơn 150 chế độ tập luyện, chiếc đồng hồ\r\n    này còn đáp ứng được nhu cầu của cả người dùng phổ thông lẫn những vận động\r\n    viên chuyên nghiệp.\r\n  </p>\r\n\r\n  <h3 dir=\"ltr\" style=\"text-align: justify\">\r\n    <b id=\"docs-internal-guid-62295f1d-7fff-e039-4ce8-31d4125916cc\"\r\n      >Viên pin trâu bò, sử dụng thoải mái</b\r\n    >\r\n  </h3>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: justify\">\r\n    Viên pin của Xiaomi Redmi Watch 5 cũng không làm mình thất vọng. Ở phiên bản\r\n    bluetooth, mình sử dụng thoải mái trong gần 3 tuần mà không phải sạc lại.\r\n    Còn với phiên bản eSIM, thời lượng pin dao động từ 10-12 ngày tùy vào mức độ\r\n    sử dụng.\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\">\r\n    <img\r\n      alt=\"Xiaomi Redmi Watch 5\"\r\n      src=\"https://cdn.viettablet.com/images/companies/1/minh-duc/xiaomi-redmi-watch-5/xiaomi-redmi-watch-5-6.jpg?1733814571045\"\r\n      style=\"width: 780px; height: 520px\"\r\n    />\r\n  </p>\r\n\r\n  <p dir=\"ltr\" style=\"text-align: center\"><em>Viên pin bền bỉ</em></p>\r\n\r\n  <p style=\"text-align: justify\">\r\n    Đặc biệt, nếu bật chế độ bộ đàm liên tục, đồng hồ vẫn có thể trụ được khoảng\r\n    25 giờ, vô cùng ấn tượng cho một thiết bị nhỏ gọn như thế này.\r\n  </p>\r\n\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi Redmi Watch 5 là một chiếc đồng hồ thông minh toàn diện, đáp ứng tốt\r\n    cả về thiết kế, phần cứng lẫn tính năng. Mặc dù bạn là người yêu thích thể\r\n    thao, cần một thiết bị hỗ trợ công việc hay chỉ đơn giản là một chiếc\r\n    smartwatch đẹp, tiện dụng thì Redmi Watch 5 đều rất đáng để trải nghiệm.\r\n  </p>\r\n</article>\r\n', 1748360269, 1748360269);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `id_danhmuc`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `giamgia`, `giadagiam`, `daban`, `averange_rating`, `trangthaisp`, `tomtat`, `noidung`, `created_time`, `last_updated`) VALUES
(9, 2, 'Xiaomi Watch S4', '3990000', 23, 'Xiaomi_Watch_S4.png', 20, 3192000, 11, 5, 1, '<ul>\r\n    <li>Thiết kế khung nhôm cao cấp và benzel tháo lắp thay đổi thời trang linh hoạt</li>\r\n    <li>Thời lượng pin sử dụng lên đến 14 ngày cho trải nghiệm liền mạch</li>\r\n    <li>Màn hình AMOLED 1.43 inch hiển thị mượt mà, sắc nét</li>\r\n    <li>Định vị GPS chính xác và cảm biến theo dõi sức khỏe toàn diện</li>\r\n    <li>Sạc nhanh tiện lợi – đầy 100% chỉ trong khoảng 45 phút</li>\r\n</ul>\r\n', '<div id=\"cpsContentSEO\">\r\n  <p style=\"text-align: justify\">\r\n    <strong>Đồng hồ Xiaomi Watch S4</strong> nổi bật với màn hình\r\n    <strong>AMOLED 1.43 inch</strong> sắc nét,\r\n    <strong>độ sáng lên đến 2000 nits</strong> cùng thời lượng\r\n    <strong>pin ấn tượng lên đến 15 ngày.</strong> Với Xiaomi HyperOS 2, mang\r\n    lại trải nghiệm điều khiển thông minh. Đồng hồ trang bị các tính năng sức\r\n    khỏe như đo nhịp tim, theo dõi giấc ngủ, bài tập hít thở giúp người dùng\r\n    chăm sóc sức khỏe toàn diện.\r\n  </p>\r\n  <div data-v-18334516=\"\" class=\"table-content\">\r\n    \r\n  </div>\r\n  <h2 style=\"scroll-margin: 154px\" id=\"vi-sao-nen-mua-dong-ho-xiaomi-watch-s4\">\r\n    <strong>Vì sao nên mua đồng hồ Xiaomi Watch S4?</strong>\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi Watch S4 không chỉ đơn thuần là một chiếc đồng hồ thông minh, mà còn\r\n    là một người bạn đồng hành lý tưởng trong cuộc sống hiện đại. Dưới đây là\r\n    những tính năng nổi bật của XWatch S4:\r\n  </p>\r\n  <ul style=\"text-align: justify\">\r\n    <li>\r\n      <p>\r\n        Viền bezel có thể thay đổi và núm vặn xoay hiện đại, dễ dàng cá nhân hóa\r\n        theo phong cách riêng. Màn hình AMOLED 1.43 inch sắc nét với độ sáng lên\r\n        đến 2000 nits, hiển thị tốt ngay cả dưới ánh nắng gắt.\r\n      </p>\r\n    </li>\r\n    <li>\r\n      <p>\r\n        Đồng hồ Xiaomi S4 còn có thời lượng pin ấn tượng cho thời gian hoạt động\r\n        đến 15 ngày ở chế độ thông thường.\r\n      </p>\r\n    </li>\r\n    <li>\r\n      <p>\r\n        Hệ điều hành Xiaomi HyperOS 2 giúp bạn quản lý thiết bị trong hệ sinh\r\n        thái Xiaomi dễ dàng, thao tác cử chỉ tiện lợi.\r\n      </p>\r\n    </li>\r\n    <li><p>Micro kép giảm tiếng ồn, cho cuộc gọi Bluetooth rõ ràng hơn</p></li>\r\n  </ul>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/dong-ho-thong-minh-xiaomi-watch-s4-1_1.png\"\r\n      alt=\"Vì sao nên mua đồng hồ Xiaomi Watch S4?\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <h2 id=\"xiaomi-watch-s4-co-gia-bao-nhieu\" style=\"scroll-margin: 154px\">\r\n    <strong>Xiaomi Watch S4 có giá bao nhiêu?</strong><em></em>\r\n  </h2>\r\n  <p></p>\r\n  <div style=\"width: 100%\">\r\n    <table\r\n      class=\"seo-table seo-product-price table is-bordered is-narrow is-hoverable is-fullwidth\"\r\n    >\r\n      <thead>\r\n        <tr>\r\n          <td style=\"width: 46%\">\r\n            <p><strong>Tên sản phẩm</strong></p>\r\n          </td>\r\n          <td style=\"width: 27%\">\r\n            <p><strong>Giá bán</strong></p>\r\n          </td>\r\n          <td style=\"width: 27%\">\r\n            <p><strong>Giá thu cũ lên đời</strong></p>\r\n          </td>\r\n        </tr>\r\n      </thead>\r\n      <tbody>\r\n        <tr style=\"width: 100%\">\r\n          <td style=\"width: 46%\"><p>Đồng hồ thông minh Xiaomi Watch S4</p></td>\r\n          <td style=\"width: 27%\"><p>3.990.000đ</p></td>\r\n          <td style=\"width: 27%\"><p>2.990.000đ</p></td>\r\n        </tr>\r\n      </tbody>\r\n    </table>\r\n  </div>\r\n  <p></p>\r\n  <p>\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-10.jpg\"\r\n      alt=\"Xiaomi Watch S4 có giá bao nhiêu?\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p>\r\n    Giá bán chưa bao gồm các chương trình ưu đãi theo từng thời điểm, chiết khấu\r\n    member, hỗ trợ thu cũ - đổi mới,...Để biết giá bán chi tiết của Xiaomi\r\n    Watch S4, khách hàng có thể tham khảo trực tiếp tại cửa hàng phân phối gần\r\n    nhất hoặc số hot line.\r\n  </p>\r\n  <h2\r\n    id=\"so-sanh-xiaomi-watch-s4-vs-xiaomi-watch-s3\"\r\n    style=\"scroll-margin: 154px\"\r\n  >\r\n    <strong>So sánh Xiaomi Watch S4 vs Xiaomi Watch S3</strong>\r\n  </h2>\r\n  <p>\r\n    So với phiên bản tiền nhiệm, đồng hồ thông minh Watch S4 sở hữu những nâng\r\n    cấp nổi bật nào? Dưới đây là bảng so sánh chi tiết cấu hình Watch S3 và\r\n    Xiaomi S4 để người dùng nắm rõ hơn về sự khác biệt giữa hai sản phẩm này:\r\n  </p>\r\n  <div align=\"left\" dir=\"ltr\">\r\n    <table>\r\n      <colgroup>\r\n        <col />\r\n        <col />\r\n        <col />\r\n      </colgroup>\r\n      <tbody>\r\n        <tr>\r\n          <td></td>\r\n          <td>\r\n            <p dir=\"ltr\" style=\"text-align: center\">\r\n              <strong>Xiaomi Watch S4</strong>\r\n            </p>\r\n          </td>\r\n          <td>\r\n            <p dir=\"ltr\" style=\"text-align: center\">\r\n              <strong>Xiaomi Watch S3</strong>\r\n            </p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Màn hình</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>AMOLED 1.43 inch</span></p>\r\n            <p dir=\"ltr\"><span>466 x 466 pixel, 326 PPI</span></p>\r\n            <p dir=\"ltr\"><span>60Hz</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>AMOLED 1.43 inch</span></p>\r\n            <p dir=\"ltr\"><span>466 x 466 pixel, 326 PPI</span></p>\r\n            <p dir=\"ltr\"><span>60Hz</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Độ sáng màn hình</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Độ sáng HBM: 1500 nits</span></p>\r\n            <p dir=\"ltr\"><span>Độ sáng tối đa: 2200 nits</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>600 nits</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Dây đeo</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Có thể tháo rời</span></p>\r\n            <p dir=\"ltr\"><span>Dây đeo cao su fluoride</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Có thể tháo rời</span></p>\r\n            <p dir=\"ltr\"><span>Dây đeo cao su fluoride</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Chống nước</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>5ATM</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>5ATM</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Theo dõi sức khoẻ</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Đo nhịp tim</span></p>\r\n            <p dir=\"ltr\"><span>Độ bão hoà oxy trong máu</span></p>\r\n            <p dir=\"ltr\"><span>Mức độ căng thẳng</span></p>\r\n            <p dir=\"ltr\"><span>Giấc ngủ</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Đo nhịp tim</span></p>\r\n            <p dir=\"ltr\"><span>Độ bão hoà oxy trong máu</span></p>\r\n            <p dir=\"ltr\"><span>Mức độ căng thẳng</span></p>\r\n            <p dir=\"ltr\"><span>Giấc ngủ</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Hỗ trợ luyện tập</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>150 chế độ thể thao</span></p>\r\n            <p dir=\"ltr\"><span>GPS độc lập</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>150 chế độ thể thao</span></p>\r\n            <p dir=\"ltr\"><span>GPS độc lập</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Bluetooth</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Bluetooth 5.3</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Bluetooth 5.2</span></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Pin</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Dung lượng 486mAh</span></p>\r\n            <p dir=\"ltr\"><span>15 ngày</span></p>\r\n            <p dir=\"ltr\"><span>Sạc nhanh</span></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p dir=\"ltr\"><span>Dung lượng 486mAh</span></p>\r\n            <p dir=\"ltr\"><span>14.5 - 15 ngày</span></p>\r\n            <p dir=\"ltr\"><span>Sạc nhanh</span></p>\r\n          </td>\r\n        </tr>\r\n      </tbody>\r\n    </table>\r\n  </div>\r\n  <p><em></em></p>\r\n  <h2\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"dong-ho-xiaomi-watch-s4--trai-nghiem-muot-ma-thong-minh\"\r\n  >\r\n    <strong>Đồng hồ Xiaomi Watch S4 – Trải nghiệm mượt mà, thông minh</strong>\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    <strong>Đồng hồ thông minh Xiaomi Watch S4</strong> đã nhanh chóng trở thành\r\n    tâm điểm chú ý của những người yêu công nghệ và sức khỏe. Với thiết kế thời\r\n    trang, tính năng đa dạng và mức giá hấp dẫn, chiếc smartwatch này sẽ mang\r\n    đến trải nghiệm hoàn toàn mới cho người dùng.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"dam-chat-ca-nhan-hoa-cung-thiet-ke-tinh-te-va-tien-loi\"\r\n  >\r\n    <strong>Đậm chất cá nhân hóa cùng thiết kế tinh tế và tiện lợi</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ Xiaomi S4 nổi bật với thiết kế viền bezel có thể thay đổi, mang đến\r\n    sự linh hoạt trong việc tùy chỉnh theo phong cách cá nhân. Thân đồng hồ sử\r\n    dụng công nghệ bảo vệ môi trường PVD, mang đến vẻ ngoài sang trọng và thân\r\n    thiện với môi trường.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/dong-ho-thong-minh-xiaomi-watch-s4-2_1.png\"\r\n      alt=\"Đậm chất cá nhân hóa cùng thiết kế tinh tế và tiện lợi\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Núm vặn xoay được nâng cấp với cảm ứng tinh tế và độ rung rõ ràng, giúp\r\n    người dùng thao tác dễ dàng. Với màn hình AMOLED 1.43 inch cùng độ sáng lên\r\n    đến 2000 nits, Xiaomi Watch S4 mang lại hình ảnh sống động ngay cả dưới ánh\r\n    nắng gắt.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Phần núm dạng cổ điển trên đồng hồ có thể hỗ trợ đồng thời thao tác bấm và\r\n    xoay. Trong đó, thao tác nhấn núm sẽ dùng để bật/tắt màn hình. Thao tác xoay\r\n    sẽ hỗ trợ người dùng lựa chọn, truy cập các tiện ích hay lướt xem tin nhắn,\r\n    lịch sử luyện tập,...Nhờ vậy mà trải nghiệm điều khiển Xiaomi S4 có tính\r\n    trực quan cao hơn.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-2.jpg\"\r\n      alt=\"Đồng hồ Xiaomi Watch S4 – Trải nghiệm mượt mà, thông minh\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Phần khung hợp kim nhôm vừa mang tới độ bóng cao, tạo sự nổi bật cho chiếc\r\n    Xiaomi Watch S4. Đồng thời, chất liệu này còn đảm bảo được độ bền bỉ cao để\r\n    người dùng thoải mái sử dụng trong các hoạt động thể chất. Kèm theo đó là\r\n    nhiều màu sắc cùng khả năng kết hợp phụ kiện linh hoạt giúp người dùng tùy\r\n    biến đồng hồ theo sở thích cá nhân.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"cong-cu-quan-ly-suc-khoe-da-chieu-cung-ho-tro-tap-luyen-da-dang\"\r\n  >\r\n    <strong\r\n      >Công cụ quản lý sức khỏe đa chiều cùng hỗ trợ tập luyện đa dạng</strong\r\n    >\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    <strong>Xiaomi Watch S4</strong> được trang bị mô-đun theo dõi nhịp tim và\r\n    đo độ bão hòa oxy trong máu với độ chính xác cao. Tính năng này không chỉ\r\n    giúp bạn phát hiện kịp thời các vấn đề sức khỏe tiềm ẩn mà còn hỗ trợ điều\r\n    chỉnh lối sống lành mạnh hơn.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/dong-ho-thong-minh-xiaomi-watch-s4-3_1.png\"\r\n      alt=\"Công cụ quản lý sức khỏe đa chiều cùng hỗ trợ tập luyện đa dạng\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Thiết bị đa dạng chế độ thể thao, phù hợp với mọi sở thích và trình độ luyện\r\n    tập. Bên đó còn có chế độ thể thao chuyên nghiệp nâng cao, hỗ trợ nhận dạng\r\n    tư thế chạy, phát hiện ngã ở chế độ trượt tuyết.\r\n  </p>\r\n  <h3 style=\"scroll-margin: 154px\" id=\"thoi-luong-pin-len-den-15-ngay\">\r\n    <strong>Thời lượng pin lên đến 15 ngày</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ Xiaomi Watch S4 sở hữu viên pin 486mAh. Với nhu cầu sử dụng thông\r\n    thường, viên pin này sẽ giúp Watch S4 có thể hoạt động liên tục tới 15 ngày.\r\n    Nhờ vậy mà người dùng có thể sử dụng đồng hồ trong các chuyến đi công tác,\r\n    du lịch ngắn ngày mà không phải mang theo bộ sạc.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-3.jpg\"\r\n      alt=\"Thời lượng pin lên đến 15 ngày\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Không chỉ sở hữu thời lượng hoạt động bền bỉ, Xiaomi Watch S4 còn được tích\r\n    hợp khả năng sạc pin nhanh chóng. Thiết bị đeo tay thông minh của Xiaomi có\r\n    thể tiếp tục sử dụng tới 2 ngày chỉ với 5 phút sạc. Điều này giúp người dùng\r\n    có thể tranh thủ sạc pin Watch S4 cả khi thực hiện các hoạt động vệ sinh cá\r\n    nhân như đánh răng, tắm gội,...\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"man-hinh-amoled-ruc-ro-do-sang-toi-da-den-2200-nits\"\r\n  >\r\n    <strong>Màn hình AMOLED rực rỡ, độ sáng tối đa đến 2200 nits</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi đã trang bị cho phiên bản Watch S4 của hãng một màn hình AMOLED 1.43\r\n    inch 60Hz, 466 x 466 pixel. Nhờ đó mà các thông báo, biểu tượng, thông tin\r\n    luyện tập hay các hiệu ứng cuộn lướt,...sẽ luôn có được độ sắc nét, mượt mà.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-4.jpg\"\r\n      alt=\"Màn hình AMOLED rực rỡ, độ sáng tối đa đến 2200 nits\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Độ sáng toàn màn hình (Độ sáng HBM) của Xiaomi Watch S4 là 1500 nits. Ngoài\r\n    ra, độ sáng cực đại mà màn hình AMOLED của Watch S4 có khả năng đạt tới là\r\n    2200 nits. Điều này giúp cho Xiaomi S4 có thể hiển thị rõ ràng mọi nội dung\r\n    mà không bị ảnh hưởng bởi môi trường xung quanh.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ Watch S4 còn đem đến hàng trăm mặt đồng hồ được cá nhân hoá từ hiện\r\n    đại đến cổ điển, giúp dễ dàng thay đổi và tuỳ chình phù hợp với sở thích.\r\n  </p>\r\n  <h3 style=\"scroll-margin: 154px\" id=\"theo-doi-suc-khoe-trong-nhay-mat\">\r\n    <strong>Theo dõi sức khoẻ trong nháy mắt</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ thông minh Xiaomi Watch S4 được trang bị một module cảm biến, hỗ trợ\r\n    theo dõi nhịp tim. Kèm theo đó là thuật toán nhịp tim do chính Xiaomi tự\r\n    phát triển nhằm cải thiện độ chính xác trên đồng hồ Xiaomi tới 98% trong\r\n    nhiều tình huống như tập luyện, ngồi,...\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-5.jpg\"\r\n      alt=\"Theo dõi sức khoẻ trong nháy mắt\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Ngoài nhịp tim, mẫu đồng hồ thông minh mới của Xiaomi còn hỗ trợ theo dõi\r\n    nhiều chỉ số khác như mức độ căng thẳng, nồng độ oxy trong máu, giấc\r\n    ngủ,...Xiaomi còn tích hợp các bài tập thở, tính năng phát hiện té ngã để\r\n    người dùng có được sự an toàn và quản lý sức khỏe tiện lợi hơn.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <strong style=\"font-size: 1.17em\"\r\n      >Hơn 150 chế độ thể thao, lý tưởng cho mọi hoạt động</strong\r\n    ><em></em>\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ thông minh Xiaomi Watch S4 còn là mẫu đồng hồ được tối ưu cho nhu\r\n    cầu luyện tập. Thiết bị đeo tay thông minh Xiaomi này được tích hợp hơn 150\r\n    chế độ tập luyện và bộ môn thể thao khác nhau. Nhờ đó mà đồng hồ sẽ luôn có\r\n    thể tối ưu hóa nhu cầu luyện tập bất kể đó là người dùng bán chuyên hay\r\n    chuyên nghiệp.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-6.jpg\"\r\n      alt=\"Hơn 150 chế độ thể thao, lý tưởng cho mọi hoạt động\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi còn tích hợp hệ thống GPS độc lập cho phiên bản đồng hồ thông minh\r\n    Watch S4 của hãng. Con chip GNSS băng tần kép L1+L5 mang tới khả năng xác\r\n    định chính xác vị trí thông qua hệ thống 5 vệ tinh. Điều này giúp cho các\r\n    hoạt động thể chất ngoài trời sẽ được ghi nhận chính xác mà không bị ảnh\r\n    hưởng bởi địa hình.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"du-lieu-suc-khoe-trong-tam-tay-voi-ung-dung-mi-fitness\"\r\n  >\r\n    <strong>Dữ liệu sức khỏe trong tầm tay với ứng dụng Mi Fitness</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi Watch S4 sử dụng hệ điều hành Xiaomi HyperOS 2 mới nhất để tương\r\n    thích tốt với hệ điều hành Android 8.0 và iOS 14 trở lên qua ứng dụng Mi\r\n    Fitness. Phần mềm được tối ưu hóa cả về giao diện lẫn ngôn ngữ để người dùng\r\n    có được trải nghiệm vô cùng trực quan.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-7.jpg\"\r\n      alt=\"Dữ liệu sức khỏe trong tầm tay với ứng dụng Mi Fitness\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Thông qua ứng dụng Mi Fitness, người dùng sẽ có thể theo dõi mọi chỉ số về\r\n    sức khoẻ được Xiaomi Watch S4 hỗ trợ theo dõi. Với thuật toán do chính\r\n    Xiaomi phát triển, các chỉ số sức khỏe sẽ được theo dõi và phân tích một\r\n    cách chính xác.\r\n  </p>\r\n  <h3 style=\"scroll-margin: 154px\" id=\"trung-tam-thong-minh-xiaomi\">\r\n    <strong>Trung tâm thông minh Xiaomi</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi HyperOS 2 còn mở ra nhiều tiện ích khác cho chiếc Watch S4. Thông qua\r\n    đồng hồ, người dùng sẽ sử dụng nhiều tiện ích như chụp ảnh từ xa, cài đặt\r\n    tai nghe, tìm kiếm điện thoại, nghe/gọi trực tiếp,...Đồng hỗ cũng hỗ trợ\r\n    điều khiển một số tính năng như từ chối cuộc gọi, chụp ảnh từ xa,...với các\r\n    thao tác tay nhanh tiện lợi.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Để có thể kích hoạt sử dụng bằng cử chỉ, người dùng phải đứng yên không di\r\n    chuyển, đặt cẳng tay song song với lòng ngực. Đảm bảo màn hình đang hoạt\r\n    động cũng như hiển thị mặt của đồng hồ hoặc trong trang chức năng.&nbsp;\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-9.jpg\"\r\n      alt=\"Trung tâm thông minh Xiaomi\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ Watch S4 của Xiaomi còn hỗ trợ kết nối với Xiaomi Smart Hub dễ dàng\r\n    điều khiển các thiết bị thông minh Xiaomi trong nhà. Nhờ đó, mẫu đồng hồ\r\n    thông minh tới từ Xiaomi còn trở thành một trung tâm quản lý và sử dụng linh\r\n    hoạt.\r\n  </p>\r\n  <h3 style=\"scroll-margin: 154px\" id=\"thao-tac-nfc-nhanh-chong\">\r\n    <strong>Thao tác NFC nhanh chóng</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Không cần dùng tiền mặt hay tiếp xúc, chỉ cần một cái chạm từ đồng hồ Xiaomi\r\n    Watch S4 lên thiết bị thanh toán là có thể sử dụng được.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    ***Note: Hiện chưa khả dụng ở Việt Nam. Các nước có thể sử dụng được:\r\n  </p>\r\n  <ul>\r\n    <li>Austria</li>\r\n    <li>United Kingdom</li>\r\n    <li>Belarus</li>\r\n    <li>Germany</li>\r\n    <li>Italy</li>\r\n    <li><span>Sweden</span></li>\r\n    <li><span>,...</span></li>\r\n  </ul>\r\n  <h2\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"mot-so-cau-hoi-thuong-gap-cho-xiaomi-watch-s4\"\r\n  >\r\n    <strong>Một số câu hỏi thường gặp cho Xiaomi Watch S4</strong>\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi Watch S4 có kháng nước không? Hệ điều hành nào có thể tương thích?\r\n    Dưới đây là tổng hợp những thắc mắc thường gặp đối với sản phẩm đeo tay\r\n    thông minh Xiaomi S4:\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"xiaomi-watch-s4-co-kha-nang-chong-nuoc-khong\"\r\n  >\r\n    <strong>Xiaomi Watch S4 có khả năng chống nước không?</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ thông minh Xiaomi S4 được hoàn thiện theo chuẩn kháng nước 5ATM,\r\n    tương đương khả năng chịu độ sâu nước lên tới 50m (ở điều kiên thử nghiệm).\r\n    Nhờ vậy mà người dùng hoàn toàn có thể sử dụng đồng hồ trong các hoạt động\r\n    thể thao ở vùng nước nông.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-11.jpg\"\r\n      alt=\"Một số câu hỏi thường gặp cho Xiaomi Watch S4\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Tuy nhiên theo Xiaomi khuyến cáo, khả năng kháng nước chuẩn 5ATM không phải\r\n    là vĩnh viễn. Vậy nên người dùng cần tránh sử dụng Watch S4 ở khi tắm nước\r\n    nóng, xông hơi, tham gia hoạt động thể thao tốc độ cao, lặn hay tiếp xúc với\r\n    các loại hóa chất.\r\n  </p>\r\n  <h2\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"dieu-khien-cam-ung-chong-am-tren-dong-ho-xiaomi-watch-s4-la-gi\"\r\n  >\r\n    <strong\r\n      >Điều khiển cảm ứng chống ẩm trên đồng hồ Xiaomi Watch S4 là gì?</strong\r\n    >\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    Đây là một thuật toán mới được tích hợp trên chiếc đồng hồ Xiaomi S4 lần\r\n    này, cho phép tránh các thao tác ngoài ý muốn do giọt nước gây ra.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"xiaomi-watch-s4-su-dung-he-dieu-hanh-nao\"\r\n  >\r\n    <strong>Xiaomi Watch S4 sử dụng hệ điều hành nào?</strong>\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Xiaomi S4 được cài đặt mặc định hệ điều hành Xiaomi HyperOS 2 mới nhất hiện\r\n    nay. Hệ điều hành này mang tới khả năng kết nối không gián đoạn giữa đồng hồ\r\n    và các thiết bị thông minh.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-12.jpg\"\r\n      alt=\"Xiaomi Watch S4 sử dụng hệ điều hành nào?\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Đồng thời, S4 còn mang tới khả năng điều khiển tiện lợi hơn với hệ điều hành\r\n    HyperOS 2. Thay vì thao tác trực tiếp qua nút bấm vật lý hay mặt đồng hồ,\r\n    người dùng còn có thể cài đặt các thao tác tay nhanh.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"xiaomi-watch-s4-co-the-thay-doi-day-deo-va-vong-bezel-khong\"\r\n  >\r\n    <strong\r\n      >Xiaomi Watch S4 có thể thay đổi dây đeo và vòng bezel không?</strong\r\n    >\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Đồng hồ thông minh Xiaomi Watch S4 hoàn toàn có thể thay đổi dây đeo và vòng\r\n    bezel. Tuỳ vào phiên bản mà dây đeo của đồng hồ Xiaomi S4 có thể điều chỉnh\r\n    từ 140mm đến 210mm. Người dùng có thể lựa chọn nhiều loại dây đeo với các\r\n    chất liệu như cao su, da, thép không gỉ,...để thay thế và sử dụng.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-1.jpg\"\r\n      alt=\"Xiaomi Watch S4 có thể thay đổi dây đeo và vòng bezel không?\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Một trong những điểm khác biệt trong thiết kế của đồng hồ Xiaomi Watch S4\r\n    với nhiều sản phẩm smartwatch khác đó là khả năng tháo, lắp vòng bezel. Khả\r\n    năng tháo rời vòng bezel này sẽ giúp cho thiết bị có được tính linh hoạt cao\r\n    để phù hợp với nhiều sở thích, phong cách của người dùng.\r\n  </p>\r\n  <h3\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"xiaomi-watch-s4-co-tuong-thich-voi-dien-thoai-android-va-ios-khong\"\r\n  >\r\n    <strong\r\n      >Xiaomi Watch S4 có tương thích với điện thoại Android và iOS\r\n      không?</strong\r\n    >\r\n  </h3>\r\n  <p style=\"text-align: justify\">\r\n    Với hệ điều hành Xiaomi HyperOS 2, đồng hồ thông minh Xiaomi S4 hiện có thể\r\n    tương thích tốt với cả điện thoại Android lẫn iPhone. Đồng hồ hỗ trợ ghép\r\n    nối với các mẫu điện thoại sử dụng Android 8.0 hoặc iOS 14 trở lên.\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    <img\r\n      src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Dong-ho-thong-minh-xiaomi-watch-s4-14.jpg\"\r\n      alt=\"Xiaomi Watch S4 có tương thích với điện thoại Android và iOS không?\"\r\n      loading=\"lazy\"\r\n    />\r\n  </p>\r\n  <p style=\"text-align: justify\">\r\n    Để kết nối với Watch S4 với điện thoại, người dùng sẽ cần sử dụng tới ứng\r\n    dụng Mi Fitness đi kèm đồng hồ. Ngoài việc hỗ trợ kết nối, ứng dụng Mi\r\n    Fitness còn giúp người dùng dễ dàng theo dõi các thông số, tùy chỉnh hình\r\n    nền, cài đặt các tiện ích mở rộng có trên đồng hồ.\r\n  </p>\r\n  <h2\r\n    style=\"scroll-margin: 154px\"\r\n    id=\"cach-ket-noi-dong-ho-xiaomi-watch-s4-voi-dien-thoai\"\r\n  >\r\n    <strong>Cách kết nối đồng hồ Xiaomi Watch S4 với điện thoại</strong>\r\n  </h2>\r\n  <p style=\"text-align: justify\">\r\n    Việc kết nối Watch S4 với điện thoại giúp bạn dễ dàng quản lý thiết bị, theo\r\n    dõi sức khỏe và trải nghiệm đầy đủ các tính năng thông minh. Dưới đây là\r\n    hướng dẫn chi tiết:\r\n  </p>\r\n  <ul style=\"text-align: justify\">\r\n    <li>\r\n      <p>\r\n        <strong>Bước 1:</strong> Bạn cần tải ứng dụng Mi Fitness để quản lý và\r\n        kết nối đồng hồ với điện thoại. Trên đồng hồ S4, chọn ngôn ngữ và quét\r\n        mã QR hiển thị trên màn hình bằng điện thoại để tải ứng dụng. Hoặc bạn\r\n        cũng có thể tải trực tiếp từ CH Play nếu sử dụng Android hoặc App Store\r\n        nếu dùng iPhone.\r\n      </p>\r\n    </li>\r\n    <li>\r\n      <p>\r\n        <strong>Bước 2:</strong> Mở ứng dụng và nhấn “Đồng ý”, sau đó đăng nhập\r\n        hoặc đăng ký tài khoản.\r\n      </p>\r\n    </li>\r\n    <li>\r\n      <p>\r\n        <strong>Bước 3:</strong> Bật Bluetooth trên điện thoại và mở ứng dụng,\r\n        vào mục “Hồ sơ”, chọn “Thêm thiết bị” và chọn “Xiaomi Watch S4” từ danh\r\n        sách. Khi ứng dụng yêu cầu quyền truy cập, hãy nhấn “Cho phép”. Đồng\r\n        thời, trên đồng hồ Watch S4, màn hình sẽ hiển thị yêu cầu ghép nối, bạn\r\n        chỉ cần nhấn dấu tích để xác nhận.\r\n      </p>\r\n    </li>\r\n  </ul>\r\n</div>\r\n', 1748360405, 1748360405),
(10, 2, 'Xiaomi Smart Band 9 Active', '590000', 39, 'Xiaomi_Smart_Band_9_Active.png', 10, 531000, 21, 5, 1, '<ul>\r\n    <li>Màn hình TFT lớn 1.47 inch cho trải nghiệm hiển thị rõ ràng, sống động</li>\r\n    <li>Thời lượng pin lên đến 14 ngày cho sử dụng lâu dài không gián đoạn</li>\r\n    <li>Theo dõi nhịp tim, giấc ngủ và mức SpO₂ liên tục 24/7</li>\r\n    <li>Hỗ trợ hơn 100 chế độ luyện tập cho đa dạng hoạt động thể thao</li>\r\n    <li>Khả năng kháng nước 5ATM – thoải mái khi bơi lội hoặc dưới mưa</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\r\n  <div class=\"ksp-content p-2 mb-2\">\r\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\r\n    <div>\r\n      <ul>\r\n        <li>\r\n          Màn hình TFT rực rỡ, cung cấp hình ảnh rõ nét, giúp người dùng dễ dàng\r\n          theo dõi thông tin ngay cả dưới ánh sáng mạnh.\r\n        </li>\r\n        <li>\r\n          Trang bị cảm biến theo dõi nhịp tim, giấc ngủ và chu kỳ kinh nguyệt,\r\n          giúp người dùng quản lý sức khỏe một cách hiệu quả.\r\n        </li>\r\n        <li>\r\n          Hỗ trợ nhiều chế độ thể thao khác nhau, từ chạy bộ đến đạp xe, giúp\r\n          người dùng theo dõi và cải thiện hiệu suất tập luyện.\r\n        </li>\r\n        <li>\r\n          Khả năng chống nước 5ATM, thiết bị cho phép người dùng sử dụng khi bơi\r\n          lội hoặc trong những điều kiện thời tiết ẩm ướt.\r\n        </li>\r\n      </ul>\r\n    </div>\r\n  </div>\r\n  <div id=\"cpsContentSEO\">\r\n    <p style=\"text-align: justify\">\r\n      <strong>Vòng đeo tay Xiaomi Mi Band 9 Active </strong> sở hữu màn hình TFT\r\n      1.47 inch với độ phân giải 172 x 320 pixel, mang lại trải nghiệm hiển thị\r\n      rõ nét. Với thời lượng pin lên đến 18 ngày ở chế độ thông thường, sản phẩm\r\n      đảm bảo đồng hành cùng bạn trong các hoạt động hằng ngày. Ngoài ra, tính\r\n      năng theo dõi sức khỏe và 50 chế độ luyện tập của Mi Band 9 Active cũng\r\n      giúp bạn chăm sóc bản thân hiệu quả hơn, đồng thời kích hoạt tối đa tiềm\r\n      năng vận động.\r\n    </p>\r\n    \r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"so-sanh-xiaomi-mi-band-9-active-vs-xiaomi-mi-band-8-active\"\r\n    >\r\n      <strong\r\n        >So sánh&nbsp;Xiaomi Mi Band 9 Active vs&nbsp;Xiaomi Mi Band 8\r\n        Active</strong\r\n      >\r\n    </h2>\r\n    <p>\r\n      Xiaomi Mi Band 9 Active kế thừa ưu điểm trên Mi Band 8 Active và có cải\r\n      tiến 1 số điểm mới nhằm nâng cao trải nghiệm người dùng.&nbsp; Xem chi\r\n      tiết qua bảng sau\r\n    </p>\r\n    <table class=\"table table-bordered\">\r\n      <tbody>\r\n        <tr class=\"success\">\r\n          <td>\r\n            <p><strong>Thông số kỹ thuật</strong></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p><strong>Xiaomi Mi Band 9 Active</strong></p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p><strong>Xiaomi Mi Band 8 Active</strong></p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Màn hình</p></td>\r\n          <td style=\"text-align: center\"><p>TFT LCD 1.47 inch</p></td>\r\n          <td style=\"text-align: center\"><p>TFT LCD 1.47 inch</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Độ phân giải</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>172 x 320 pixel,</p>\r\n            <p>247 PPI</p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p>172 x 320 pixel,</p>\r\n            <p>247 PPI</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Dây đeo</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>Có thể tháo rời,</p>\r\n            <p>Đen, Trắng Be, Hồng</p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p>Có thể tháo rời,</p>\r\n            <p>Trắng ngà｜Ô liu｜Xanh dương｜Đen｜Hồng</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Chống nước</p></td>\r\n          <td style=\"text-align: center\"><p>5ATM</p></td>\r\n          <td style=\"text-align: center\"><p>5ATM</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Theo dõi sức khỏe</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>Theo dõi nhịp tim,</p>\r\n            <p>Theo dõi SpO₂,</p>\r\n            <p>Theo dõi giấc ngủ,</p>\r\n            <p>Quản lý sức khỏe phụ nữ,</p>\r\n            <p>Đo mức độ căng thẳng</p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p>Theo dõi nhịp tim,</p>\r\n            <p>Theo dõi SpO₂,</p>\r\n            <p>Theo dõi giấc ngủ,</p>\r\n            <p>Quản lý sức khỏe phụ nữ,</p>\r\n            <p>Đo mức độ căng thẳng</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Hỗ trợ tập luyện</p></td>\r\n          <td style=\"text-align: center\"><p>Hỗ trợ 50 chế độ luyện tập</p></td>\r\n          <td style=\"text-align: center\"><p>Hơn 50 chế độ luyện tập</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Bluetooth</p></td>\r\n          <td style=\"text-align: center\"><p>Bluetooth 5.3 BLE</p></td>\r\n          <td style=\"text-align: center\"><p>Bluetooth 5.1 BLE</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Thời gian sử dụng</p></td>\r\n          <td style=\"text-align: center\">\r\n            <p>Dung lượng pin 300mAh</p>\r\n            <p>Lên đến 18 ngày&nbsp;</p>\r\n          </td>\r\n          <td style=\"text-align: center\">\r\n            <p>Dung lượng pin 210mAh</p>\r\n            <p>Lên đến 14 ngày</p>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Mặt đồng hồ</p></td>\r\n          <td style=\"text-align: center\"><p>Hơn 100 mặt tuỳ chỉnh</p></td>\r\n          <td style=\"text-align: center\"><p>Hơn 100 mặt tuỳ chỉnh</p></td>\r\n        </tr>\r\n        <tr>\r\n          <td style=\"text-align: center\"><p>Kích thước</p></td>\r\n          <td style=\"text-align: center\"><p>45,9 x 26,94 x 9,99mm</p></td>\r\n          <td style=\"text-align: center\"><p>42,81mm x 25,42mm x 9,99mm</p></td>\r\n        </tr>\r\n      </tbody>\r\n    </table>\r\n    <p>\r\n      Có thể thấy, sản phẩm duy trì mặt đồng hồ kích thước lớn&nbsp;TFT LCD 1.47\r\n      inch, độ phân giải&nbsp;172 x 320 pixel và độ mỏng 9,99mm của&nbsp;Xiaomi\r\n      Mi Band 8 Active. Tuy nhiên, Mi Band 9 Active đã có nâng cao dung lượng\r\n      pin lên 300mAh, kéo dài thời lượng sử dụng lên đến 18 ngày.\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"vi-sao-nen-mua-vong-deo-tay-thong-minh-xiaomi-mi-band-9-active\"\r\n    >\r\n      <strong>\r\n        Vì sao nên mua vòng đeo tay thông minh Xiaomi Mi Band 9 Active?\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n    <strong>Đồng hồ thông minh Xiaom</strong>\r\n      Mi Band 9 Active là lựa chọn hàng đầu cho người dùng tìm kiếm thiết bị đa\r\n      năng và thời trang. Sản phẩm này mang đến nhiều ưu điểm vượt trội, bao\r\n      gồm:\r\n    </p>\r\n    <ul>\r\n      <li>\r\n        <strong\r\n          >Thiết kế siêu mỏng chỉ 9,99mm,&nbsp;trọng lượng chỉ 16.5g</strong\r\n        >\r\n        (chưa gồm dây), bạn có thể đeo cả ngày mà không cảm thấy nặng nề.\r\n      </li>\r\n      <li>\r\n        <strong>Màn hình TFT sắc nét:</strong> Kích thước 1.47 inch cùng độ phân\r\n        giải 172 x 320 đảm bảo hiển thị thông tin rõ ràng, dễ nhìn trong nhiều\r\n        điều kiện ánh sáng.\r\n      </li>\r\n      <li>\r\n        <strong>Đa dạng tính năng sức khỏe và thể thao:</strong> Từ theo dõi\r\n        nhịp tim, giấc ngủ đến hỗ trợ 50 chế độ luyện tập, giúp cải thiện sức\r\n        khỏe và phong độ thể thao.\r\n      </li>\r\n      <li>\r\n        <strong>Thời lượng pin ấn tượng:</strong> Lên đến 18 ngày ở chế độ thông\r\n        thường, giảm thiểu lo lắng về việc sạc pin thường xuyên.\r\n      </li>\r\n    </ul>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-1_1.jpg\"\r\n        alt=\"Vì sao nên mua vòng đeo tay thông minh Xiaomi Mi Band 9 Active?\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"dong-ho-xiaomi-mi-band-9-active-da-dang-tinh-nang-ho-tro-kieu-dang-hien-dai\"\r\n    >\r\n      <strong\r\n        >Đồng hồ Xiaomi Mi Band 9 Active - Đa dạng tính năng hỗ trợ, kiểu dáng\r\n        hiện đại\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Vòng đeo tay thông minh Xiaomi Mi Band 9 Active là một thiết bị không chỉ\r\n      đáp ứng nhu cầu theo dõi sức khỏe mà còn mang phong cách hiện đại, phù hợp\r\n      với mọi đối tượng người dùng. Phân khúc vòng tay thông minh này được kết\r\n      hợp giữa thiết kế thời thượng, trọng lượng siêu nhẹ, và các tính năng\r\n      thông minh, tạo nên sự khác biệt so với các sản phẩm cùng tầm giá.\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thiet-ke-sieu-nhe-va-mong-hoan-thien-tu-chat-lieu-cao-cap\"\r\n    >\r\n      <strong>\r\n        Thiết kế siêu nhẹ và mỏng, hoàn thiện từ chất liệu cao cấp\r\n      </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Mi Band 9 Active sở hữu thiết kế siêu mỏng với độ dày chỉ 9,99mm, mang lại\r\n      cảm giác thoải mái tối đa khi đeo. Thân máy chỉ nặng 16,5g, giúp người\r\n      dùng đeo cả ngày mà không cảm thấy khó chịu.&nbsp;\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-2_1.jpg\"\r\n        alt=\"Đồng hồ Xiaomi Mi Band 9 Active - Đa dạng tính năng hỗ trợ, kiểu dáng hiện đại\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Phần dây đeo của vòng tay được làm từ TPU mềm mại, thân thiện với da, đồng\r\n      thời có nhiều tùy chọn màu sắc như đen, trắng be và hồng, phù hợp với\r\n      phong cách cá nhân. Ngoài ra, bạn có thể dễ dàng thay đổi dây đeo và khung\r\n      viền để làm mới diện mạo sản phẩm.\r\n    </p>\r\n    \r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"toi-uu-hoa-hieu-qua-luyen-tap-voi-da-dang-che-do-do-the-thao\"\r\n    >\r\n      <strong>\r\n        Tối ưu hóa hiệu quả luyện tập với đa dạng chế độ đo thể thao\r\n      </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Với 50 chế độ thể thao tích hợp, vòng đeo tay thông minh Xiaomi Smart Band\r\n      9 Active là người bạn đồng hành lý tưởng trong hành trình nâng cao thể\r\n      lực. Các chế độ như chạy bộ, đạp xe, chèo thuyền được tối ưu hóa với thuật\r\n      toán chuyên nghiệp, mang lại dữ liệu chính xác và chi tiết. Ngoài ra, tính\r\n      năng theo dõi thời gian thực của máy cũng giúp bạn kiểm soát tiến độ luyện\r\n      tập, từ đó xây dựng kế hoạch rèn luyện phù hợp hơn với mục tiêu cá nhân.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-4_1.jpg\"\r\n        alt=\"Tối ưu hóa hiệu quả luyện tập với đa dạng chế độ đo thể thao\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"theo-doi-suc-khoe-voi-cam-bien-hien-dai-da-dang-chuc-nang\"\r\n    >\r\n      <strong>\r\n        Theo dõi sức khỏe với cảm biến hiện đại, đa dạng chức năng\r\n      </strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Mi Band 9 Active được trang bị cảm biến PPG hiện đại, giúp theo dõi nhịp\r\n      tim và mức độ oxy trong máu (SpO₂) cả ngày. Tính năng này có thể gửi cảnh\r\n      báo rung khi phát hiện mức SpO₂ thấp, giúp người dùng nhận diện sớm các\r\n      vấn đề sức khỏe tiềm ẩn.&nbsp;\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-3_1.jpg\"\r\n        alt=\"Theo dõi sức khỏe với cảm biến hiện đại, đa dạng chức năng\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Ngoài ra, đồng hồ Mi Band 9 Active còn ghi nhận và phân tích chi tiết giấc\r\n      ngủ qua các giai đoạn sâu, nông và REM. Kèm theo đó là tính năng đo mức độ\r\n      căng thẳng và quản lý sức khỏe phụ nữ là những bổ sung hữu ích cho người\r\n      dùng hiện đại.\r\n    </p>\r\n    <h3\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"thoi-luong-pin-len-den-18-ngay-khang-nuoc-5-atm\"\r\n    >\r\n      <strong> Thời lượng pin lên đến 18 ngày, kháng nước 5 ATM</strong>\r\n    </h3>\r\n    <p style=\"text-align: justify\">\r\n      Với dung lượng pin 300mAh, vòng đeo tay thông minh Xiaomi Mi Band 9 Active\r\n      có thể hoạt động liên tục 18 ngày ở chế độ thông thường và khoảng 9 ngày ở\r\n      chế độ sử dụng cao. Bên cạnh đó, thiết bị còn được đánh giá cao khi sử\r\n      dụng sạc từ tính hiện đại, giúp rút ngắn thời gian sạc xuống dưới 120\r\n      phút. Nhờ vậy mà Smart Band 9 đảm bảo sẽ luôn sẵn sàng đồng hành cùng bạn\r\n      trong mọi hoạt động hằng ngày.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-6_1.jpg\"\r\n        alt=\"Thời lượng pin lên đến 18 ngày, kháng nước 5 ATM\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Ngoài ra, thiết bị sở hữu khả năng kháng nước 5ATM, giúp người dùng thoải\r\n      mái hơn khi tham gia các hoạt động thể thao ngoài trời .\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"huong-dan-ket-noi-vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-voi-dien-thoai\"\r\n    >\r\n      <strong>\r\n        Hướng dẫn kết nối vòng đeo tay thông minh Xiaomi Mi Band 9 Active với\r\n        điện thoại\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Để trải nghiệm đầy đủ các tính năng của vòng đeo tay thông minh Xiaomi Mi\r\n      Band 9 Active, việc kết nối với điện thoại là bước không thể bỏ qua. Thao\r\n      tác kết nối rất đơn giản và chỉ mất vài phút. Chi tiết các bước như sau:\r\n    </p>\r\n    <ul>\r\n      <li>\r\n        <strong>Bước 1:</strong> Trên smartphone, bạn cần tải về, cài đặt và\r\n        thành công đăng nhập vào ứng dụng Mi Fitness.&nbsp;\r\n      </li>\r\n      <li>\r\n        <strong>Bước 2</strong>: Trong lần đầu khởi động Mi Band 9 Active, bạn\r\n        cần kết nối thiết bị với nguồn điện và lựa chọn ngôn ngữ hiển thị.&nbsp;\r\n      </li>\r\n    </ul>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-6.jpg\"\r\n        alt=\"Hướng dẫn kết nối vòng đeo tay thông minh Xiaomi Mi Band 9 Active với điện thoại\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <ul>\r\n      <li>\r\n        <strong>Bước 3:</strong> Tại giao diện chính của ứng dụng Mi Fitness\r\n        trên điện thoại, bạn chọn vào mục Thiết bị và nhấn vào Thêm Thiết Bị.\r\n      </li>\r\n      <li>\r\n        <strong>Bước 4:</strong> Trong danh sách thiết bị được hiển thị, bạn\r\n        chọn vào Mi Band 9 Active và chọn Ghép nối. Đồng thời, trên màn hình của\r\n        Mi Band 9 Active, bạn đồng ý ghép nối. Sau đó, bạn chỉ cần chờ một chút\r\n        để hệ thống ghép nối là bạn đã thành công rồi nhé!\r\n      </li>\r\n    </ul>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/vong-deo-tay-thong-minh-xiaomi-mi-band-9-active-7.jpg\"\r\n        alt=\"Hướng dẫn kết nối vòng đeo tay thông minh Xiaomi Mi Band 9 Active với điện thoại\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"mua-vong-deo-tay-xiaomi-mi-band-9-active-gia-tot-giam-them-smember-tai-cellphones\"\r\n    >\r\n      <strong>\r\n        Mua vòng đeo tay Xiaomi Mi Band 9 Active giá tốt, giảm thêm Smember tại\r\n        CellphoneS\r\n      </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      CellphoneS là điểm đến mua hàng lý tưởng được nhiều người dùng ghé tới để\r\n      sở hữu vòng đeo tay thông minh Xiaomi Mi Band 9 Active với giá cả hợp lý\r\n      và dịch vụ hàng đầu. Là một trong những chuỗi cửa hàng công nghệ uy tín\r\n      tại Việt Nam, CellphoneS mang đến các sản phẩm chính hãng, đảm bảo chất\r\n      lượng và nguồn gốc rõ ràng.\r\n    </p>\r\n    <p style=\"text-align: justify\">\r\n      Khách hàng có thể tận hưởng chính sách giá cạnh tranh cùng nhiều ưu đãi\r\n      hấp dẫn khi mua sắm tại đây. Với hệ thống cửa hàng trải dài trên cả nước,\r\n      CellphoneS cam kết sẽ mang lại sự thuận tiện và trải nghiệm mua sắm\r\n      <strong>Mi Band 9 Active chính hãng</strong> tuyệt vời cho khách hàng.\r\n    </p>\r\n  </div>\r\n</div>\r\n', 1748363930, 1748363930);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `id_danhmuc`, `tensanpham`, `giasp`, `soluong`, `hinhanh`, `giamgia`, `giadagiam`, `daban`, `averange_rating`, `trangthaisp`, `tomtat`, `noidung`, `created_time`, `last_updated`) VALUES
(11, 2, 'Xiaomi Smart Band 9 Pro', '1690000', 13, 'Xiaomi_Smart_Band_9_Pro.png', 15, 1436500, 10, 5, 1, '<ul>\r\n    <li>Màn hình AMOLED 1.74 inch siêu lớn, hiển thị mượt mà và sắc nét</li>\r\n    <li>Pin dùng lên đến 14 ngày, thoải mái sử dụng không lo gián đoạn</li>\r\n    <li>Tích hợp GPS độc lập cho khả năng định vị chính xác khi tập luyện</li>\r\n    <li>Theo dõi sức khỏe toàn diện: nhịp tim, SpO₂, giấc ngủ và căng thẳng</li>\r\n    <li>Hơn 150 chế độ luyện tập giúp cá nhân hóa thói quen vận động</li>\r\n</ul>\r\n', '<div class=\"rm-detail\">\r\n  <div id=\"gioithieu\">\r\n    <div class=\"ex_cntitle ex_cnpost\">Đánh giá chi tiết</div>\r\n    <div class=\"product-description dtshow\" style=\"height: 400px\">\r\n      <p>\r\n        <strong\r\n          >Xiaomi Band 7 Pro là thiết bị đeo tay thông minh, tích hợp GPS độc\r\n          lập, nâng cấp cảm biến SpO2, màn hình tràn viền 1.64 inch AMOLED mang\r\n          lại độ sắc nét rõ hơn, hỗ trợ Always-on Display màn hình luôn bật, độ\r\n          sáng lên tới 326 PPI. Bên cạnh đó là những tính năng hỗ trợ theo dõi\r\n          hoạt động, đo nhịp tim, kết nối với điện thoại báo tin nhắn cuộc\r\n          gọi.</strong\r\n        >\r\n      </p>\r\n      <p>\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"aligncenter wp-image-15807 size-full wptangtoc-lazy entered gtloader\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-15.jpg\"\r\n          alt=\"Xiaomi Band 7 Pro 15\"\r\n          width=\"800\"\r\n          height=\"1119\"\r\n          title=\"Xiaomi Band 7 Pro 14\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-15.jpg\"\r\n          data-sizes=\"(max-width: 800px) 100vw, 800px\"\r\n          data-ll-status=\"loaded\"\r\n          sizes=\"(max-width: 800px) 100vw, 800px\"\r\n        />\r\n      </p>\r\n      <p><strong>Đặc điểm của chiếc Xiaomi Miband 7 Pro :</strong></p>\r\n      <div\r\n        class=\"lwptoc lwptoc-baseItems lwptoc-light lwptoc-notInherit\"\r\n        data-smooth-scroll=\"1\"\r\n        data-smooth-scroll-offset=\"24\"\r\n        data-lwptoc-initialized=\"1\"\r\n      >\r\n        <div class=\"lwptoc_i\"><div class=\"lwptoc_header\"></div></div>\r\n      </div>\r\n      <h2>\r\n        <span id=\"Thiet_ke_khung_vien_kim_loai_doc_dao_sang_trong\"\r\n          >Thiết kế khung viền kim loại độc đáo sang trọng</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Xiaomi thiết kế khung viền của Band 7 Pro là dạng khung viền giả kim\r\n        loại (thực chất là hoàn thiện từ nhựa). Cạnh phải mặc dù không có nút\r\n        bấm vật lý nhưng có một lỗ micro thu âm dùng ra lệnh giọng nói.\r\n      </p>\r\n      <p>\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"aligncenter wp-image-15808 size-full wptangtoc-lazy entered gtloader\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-16.jpg\"\r\n          alt=\"Xiaomi Band 7 Pro 16\"\r\n          width=\"517\"\r\n          height=\"934\"\r\n          title=\"Xiaomi Band 7 Pro 15\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-16.jpg\"\r\n          data-sizes=\"(max-width: 517px) 100vw, 517px\"\r\n          data-ll-status=\"loaded\"\r\n          sizes=\"(max-width: 517px) 100vw, 517px\"\r\n        />\r\n      </p>\r\n      <p>\r\n        Dây đeo của Xiaomi Band 7 Pro cũng là loại dây silicon có thể tháo rời\r\n        và có nhiều tuỳ chọn màu sắc khác nhau, dễ dàng thay đổi theo sở thích\r\n        của người dùng. Dây đeo này có thiết kế dạng khoá truyền thống chứ không\r\n        phải dạng đính ghép nối như trên các dòng Mi Band truyền thống.\r\n      </p>\r\n      <p>\r\n        <img\r\n          decoding=\"async\"\r\n          class=\"aligncenter wp-image-15812 size-full wptangtoc-lazy entered gtloader\"\r\n          src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-21.jpg\"\r\n          alt=\"Xiaomi Band 7 Pro 21\"\r\n          width=\"800\"\r\n          height=\"1736\"\r\n          title=\"Xiaomi Band 7 Pro 16\"\r\n          fetchpriority=\"low\"\r\n          data-src=\"https://mihome.vn/wp-content/uploads/2022/07/Xiaomi-Band-7-Pro-21.jpg\"\r\n          data-sizes=\"(max-width: 800px) 100vw, 800px\"\r\n          data-ll-status=\"loaded\"\r\n          sizes=\"(max-width: 800px) 100vw, 800px\"\r\n        />\r\n      </p>\r\n      <h2>\r\n        <span id=\"Man_hinh_tran_vien_164_AMOLED\"\r\n          >Màn hình tràn viền 1.64″&nbsp;AMOLED</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Xiaomi Band 7 Pro sở hữu màn hình AMOLED, độ phân giải 280 x 456 pixels,\r\n        mật độ điểm ảnh đạt mức “retina” là 326PPI. Phần viền màn hình được\r\n        thiết kế tương đối mỏng nhưng lại dày hơn một chút ở cạnh dưới. Ngoài ra\r\n        màn hình này cũng được thiết kế cong nhẹ 2.5D ra các cạnh nhằm giúp thao\r\n        tác chạm và vuốt dễ dàng hơn.\r\n      </p>\r\n      \r\n      <h2>\r\n        <span id=\"Thiet_bi_ho_tro_suc_khoe_huu_ich\"\r\n          >Thiết bị hỗ trợ sức khỏe hữu ích</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Theo dõi nhịp tim,&nbsp;<strong>đo Oxy SpO2</strong>, đo mức độ căng\r\n        thẳng, đo nhịp thở, theo dõi giấc ngủ …\r\n      </p>\r\n      <p>\r\n        Theo dõi SpO2 cả ngày dài, phát hiện nồng độ Oxy dưới 90% đặc biệt hữu\r\n        ích cho những người leo núi hoặc chạy đường dài.\r\n      </p>\r\n      <p>\r\n        Theo dõi giấc ngủ 24h, giấc ngủ mắt chuyển động REM, giấc ngủ trưa, sức\r\n        khoẻ phụ nữ, nhắc nhở chu kỳ.\r\n      </p>\r\n    \r\n      <h2>\r\n        <span id=\"Thoi_luong_pin_lon_-_Day_sac_tu_tinh\"\r\n          >Thời lượng pin lớn – Dây sạc từ tính</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Thời lượng pin khoảng 12 ngày (dữ liệu thử nghiệm, có thay đổi tuỳ theo\r\n        mức độ sử dụng thực tế)<br />\r\n        Sạc từ tính, không cần tháo dây vô cùng tiện lợi\r\n      </p>\r\n      \r\n      <h2>\r\n        <span id=\"Giao_dien_mat_dong_ho_da_dang\"\r\n          >Giao diện mặt đồng hồ đa dạng</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Hơn 130 giao diện mặt đồng hồ khác nhau tha hồ tùy biến phong cách cá\r\n        tính riêng\r\n      </p>\r\n      \r\n      <h2>\r\n        <span id=\"Ho_tro_nhieu_bo_mon_the_thao_phu_hop_voi_moi_nguoi\"\r\n          >Hỗ trợ nhiều bộ môn thể thao, phù hợp với mọi người</span\r\n        >\r\n      </h2>\r\n      <p>Nâng cấp lên tới 117 chế độ luyện tập thể thao khác nhau.</p>\r\n     \r\n      \r\n      <h2>\r\n        <span id=\"Chuan_khang_nuoc_5_ATM_-_Thoa_suc_boi_loi\"\r\n          >Chuẩn kháng nước 5 ATM – Thỏa sức bơi lội</span\r\n        >\r\n      </h2>\r\n      <p>\r\n        Trang bị khả năng kháng nước 5 ATM tăng thêm độ bền cho người dùng yên\r\n        tâm sử dụng ngay cả khi bơi lặn.\r\n      </p>\r\n      \r\n      <h2><span id=\"Mot_so_tinh_nang_khac\">Một số tính năng khác :</span></h2>\r\n      <p>\r\n        GPS độc lập, cảm biến ánh sáng xung quanh, tự động điều chỉnh độ sáng\r\n        màn hình thoải mái cho mắt người nhìn.\r\n      </p>\r\n      <p>\r\n        <strong>Hỗ trợ Always On Display</strong>&nbsp;– Luôn hiển thị màn hình\r\n        xem giờ tiện lợi\r\n      </p>\r\n    </div>\r\n  </div>\r\n</div>\r\n', 1748364065, 1748364065),
(12, 2, 'Redmi Watch 5 Lite', '1290000', 31, 'Redmi_Watch_5_Lite.png', 20, 1030000, 33, 4, 1, '<ul>\r\n    <li>Màn hình AMOLED 1.83 inch hiển thị rõ nét, dễ nhìn dưới ánh sáng ngoài trời</li>\r\n    <li>Thời lượng pin lên đến 12 ngày sử dụng liên tục</li>\r\n    <li>Theo dõi nhịp tim, SpO₂, giấc ngủ và mức độ căng thẳng suốt cả ngày</li>\r\n    <li>Hơn 100 chế độ thể thao giúp bạn duy trì thói quen vận động hiệu quả</li>\r\n    <li>Kháng nước 5ATM – an tâm khi đi mưa hay bơi nhẹ</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\n  <div class=\"ksp-content p-2 mb-2\">\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\n    <div>\n      <ul>\n        <li>\n          Với màn hình lớn 1.96 inch cung cấp hình ảnh sắc nét và dễ đọc dưới\n          ánh sáng mặt trời.\n        </li>\n        <li>\n          Thiết bị hỗ trợ nhiều chế độ thể thao, giúp người dùng theo dõi hoạt\n          động tập luyện một cách chi tiết.\n        </li>\n        <li>\n          Tích hợp cảm biến nhịp tim và theo dõi giấc ngủ, cung cấp thông tin\n          hữu ích về sức khỏe.\n        </li>\n        <li>\n          Thời lượng pin tuyệt vời cho phép sử dụng lên đến 18 ngày chỉ với một\n          lần sạc.\n        </li>\n      </ul>\n    </div>\n  </div>\n  <div id=\"cpsContentSEO\">\n    <p style=\"text-align: justify\">\n      <strong>Đồng hồ Redmi Watch 5 Lite </strong> sở hữu màn hình\n      <strong>AMOLED 1.96 inch</strong> sắc nét, mang lại trải nghiệm hiển thị\n      rõ ràng ở nhiều đièu kiện ánh sáng. Với dung lượng pin lớn có thể\n      <strong>lên đến 18 ngày</strong> chỉ sau một lần sạc, phù hợp cho người\n      dùng có nhu cầu sử dụng dài ngày. Thêm vào đó, sản phẩm đồng hồ này còn\n      được ưu ái sở hữu khả năng <strong>chống nước 5AT</strong>M và tính năng\n      nghe gọi Bluetooth giúp nâng cao trải nghiệm sử dụng.\n    </p>\n    \n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"vi-sao-nen-mua-dong-ho-xiaomi-redmi-watch-5-lite\"\n    >\n      <strong> Vì sao nên mua đồng hồ Xiaomi Redmi Watch 5 Lite? </strong>\n    </h2>\n    <p style=\"text-align: justify\">\n      Với các ưu điểm vượt trội cả về tính năng tiện lợi lẫn thiết kế năng động,\n      bền bỉ và\n      <strong\n        >Redmi Watch 5 Lite</strong\n      >\n      đều được đánh giá là lựa chọn smartwatch lý tưởng mà người dùng nên sở\n      hữu. Dưới đây là 4 điểm nhấn nổi bật trên smartwatch Xiaomi Redmi Watch 5\n      Lite mà bạn có tham khảo thêm nhé!\n    </p>\n    <ul>\n      <li>\n        <strong\n          >Màn hình AMOLED kích thước&nbsp;1.96 inch, độ phân giải 410x502\n          pixel</strong\n        >\n        giúp hiển thị thông tin rõ ràng và cho phép vuốt chạm dễ dàng hơn\n      </li>\n      <li>\n        <strong>Thời lượng pin khủng:</strong> Với dung lượng lớn, đồng hồ Redmi\n        Watch 5 Lite có thể hoạt động lên đến 18 ngày chỉ sau một lần sạc\n      </li>\n      <li>\n        <strong>Theo dõi sức khỏe toàn diện:</strong> đo nhịp tim, Spo2, theo\n        dõi giấc ngủ trên Xiaomi Watch 5 Lite\n      </li>\n      <li>\n        <strong>Khả năng kháng nước 5ATM:</strong> Đem tới sự yên tâm cho người\n        dùng khi vẫn có thể được vận hành ổn định\n      </li>\n      <li>\n        <strong>GPS độc lập:&nbsp;</strong>Tích hợp 5 hệ thống GNSS:<strong\n        ></strong>GPS/GLONASS/Galileo/QZSS/Beidou\n      </li>\n      <li>\n        <strong\n          >Hỗ trợ đàm thoại qua Bluetooth, tăng cường 2 mic giảm nhiễu</strong\n        >\n      </li>\n    </ul>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/dong-ho-thong-minh-xiaomi-redmi-watch-5-lite-5.jpg\"\n        alt=\"Vì sao nên mua đồng hồ Xiaomi Redmi Watch 5 Lite?\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"so-sanh-xiaomi-redmi-watch-5-lite-vs-redmi-5-active\"\n    >\n      <strong>So sánh Xiaomi Redmi Watch 5 Lite vs Redmi 5 Active</strong>\n    </h2>\n    <p style=\"text-align: justify\">\n      Redmi Watch 5 Lite và Redmi 5 Active đều là những thiết bị đeo thông minh\n      nổi bật trong phân khúc giá rẻ. Redmi 5 Lite mang đến thiết kế hiện đại và\n      nhiều tính năng theo dõi sức khỏe, trong khi đó, Redmi 5 Active lại chú\n      trọng vào độ bền. Xem chi tiết thông số sản phẩm qua bảng sau:\n    </p>\n    <div class=\"table-container\">\n      <table\n        class=\"seo-table table is-bordered is-hoverable\"\n        style=\"min-width: 480px; overflow-x: auto\"\n      >\n        <thead>\n          <tr>\n            <td>\n              <p><strong>Thông số</strong></p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>\n                <strong>Đồng hồ thông minh Xiaomi Redmi Watch 5 Lite</strong>\n              </p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>\n                <strong>Đồng hồ thông minh Xiaomi Redmi Watch 5 Active</strong>\n              </p>\n            </td>\n          </tr>\n        </thead>\n        <tbody>\n          <tr>\n            <td>\n              <p><strong>Công nghệ màn hình</strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>AMOLED</p></td>\n            <td style=\"width: 35%\"><p>LCD</p></td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Kích thước màn hình</strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>1.96 inch</p></td>\n            <td style=\"width: 35%\"><p>2.0 inch</p></td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Đường kính mặt</strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>48.2 mm</p></td>\n            <td style=\"width: 35%\"><p>49.1 mm</p></td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Kích thước cổ tay phù hợp</strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>Hãng không công bố</p></td>\n            <td style=\"width: 35%\"><p>Hãng không công bố</p></td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Nghe,gọi </strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>Nghe gọi qua Bluetooth</p></td>\n            <td style=\"width: 35%\"><p>Nghe gọi qua Bluetooth</p></td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Tiện ích sức khoẻ </strong></p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>\n                Chế độ luyện tập, Theo dõi giấc ngủ, Đo nhịp tim, Đo lượng oxy\n                trong máu, Đếm bước chân, Đo mức độ stress, Cảnh báo nhịp tim\n                bất thường, Theo dõi chu kỳ\n              </p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>\n                Chế độ luyện tập, Theo dõi giấc ngủ, Đo nhịp tim, Đo lượng oxy\n                trong máu, Đếm bước chân, Đo mức độ stress\n              </p>\n            </td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Tương thích </strong></p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>Android 6.0 trở lên<br />iOS 11 trở lên</p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>Android 6.0 trở lên<br />iOS 11 trở lên</p>\n            </td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Thời lượng pin </strong></p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>18 ngày sử dụng liên tục (không có AOD)</p>\n            </td>\n            <td style=\"width: 35%\">\n              <p>18 ngày sử dụng liên tục (không có AOD)</p>\n            </td>\n          </tr>\n          <tr>\n            <td>\n              <p><strong>Hãng sản xuất</strong></p>\n            </td>\n            <td style=\"width: 35%\"><p>Xiaomi</p></td>\n            <td style=\"width: 35%\"><p>Xiaomi</p></td>\n          </tr>\n        </tbody>\n      </table>\n    </div>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"dong-ho-redmi-watch-5-lite-thiet-ke-ben-bi-da-dang-tinh-nang-thong-minh\"\n    >\n      <strong>\n        Đồng hồ Redmi Watch 5 Lite - Thiết kế bền bỉ, đa dạng tính năng thông\n        minh\n      </strong>\n    </h2>\n    <p style=\"text-align: justify\">\n      Đồng hồ thông minh Xiaomi\n      \n      Redmi Watch 5 Lite mang lại trải nghiệm toàn diện cho người dùng khi được\n      kết hợp ấn tượng giữa thiết kế năng động và tính năng hiện đại. Với màn\n      hình rộng, chất liệu cao cấp và khả năng chống nước 5ATM, Watch 5 Lite\n      không chỉ phù hợp với người yêu thể thao mà còn đáp ứng tốt nhu cầu sử\n      dụng hàng ngày.\n    </p>\n    <h3 style=\"scroll-margin: 154px\" id=\"kieu-dang-nang-dong-man-hinh-lon-hon\">\n      <strong> Kiểu dáng năng động, màn hình lớn hơn&nbsp;</strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Xiaomi Redmi Watch 5 Lite sở hữu thiết kế viền vuông vức, tạo nên vẻ ngoài\n      vừa mạnh mẽ vừa hiện đại. Viền cạnh của đồng hồ được chế tạo từ chất liệu\n      cao cấp, không chỉ tăng tính thẩm mỹ mà còn giúp smartwatch bền bỉ hơn. So\n      với phiên bản tiền nhiệm trước đó thì Redmi Watch 5 Lite thể hiện sự nâng\n      cấp vượt trội về màn hình lớn hơn 7.6%, đáp ứng tốt cho những người yêu\n      thích vận động.\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/dong-ho-thong-minh-xiaomi-redmi-watch-5-lite-3.jpg\"\n        alt=\"Đồng hồ Xiaomi Redmi Watch 5 Lite - Thiết kế bền bỉ, đa dạng tính năng thông minh\"\n        loading=\"lazy\"\n      />\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"dung-luong-pin-lon-cung-cap-nang-luong-cho-nhieu-ngay-chi-sau-1-lan-sac\"\n    >\n      <strong>\n        Dung lượng pin lớn, cung cấp năng lượng cho nhiều ngày chỉ sau 1 lần sạc\n      </strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Pin là một trong những yếu tố được người dùng đặc biệt quan tâm khi chọn\n      mua đồng hồ thông minh, và Redmi Watch 5 Lite không làm người dùng thất\n      vọng. Với viên pin lớn, chiếc đồng hồ thông minh Xiaomi này có thể hoạt\n      động lên đến 18 ngày chỉ sau một lần sạc, đáp ứng được mọi nhu cầu sử dụng\n      cơ bản của người dùng.&nbsp;\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/dong-ho-thong-minh-xiaomi-redmi-watch-5-lite-1.jpg\"\n        alt=\"Đồng hồ Xiaomi Redmi Watch 5 Lite - Thiết kế bền bỉ, đa dạng tính năng thông minh\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Đối với những người thường xuyên tập luyện hoặc di chuyển nhiều, thời\n      lượng pin ấn tượng này cũng giúp kéo dài và giảm thiểu tình trạng gián\n      đoạn trong quá trình sử dụng. Đây chính là điểm mạnh giúp Xiaomi Redmi\n      Watch 5 Lite trở thành lựa chọn lý tưởng cho những ai cần một thiết bị vừa\n      hiện đại, vừa bền bỉ.\n    </p>\n    <h3\n      style=\"scroll-margin: 154px\"\n      id=\"nang-cap-trai-nghiem-nguoi-dung-voi-da-dang-tinh-nang-hien-dai\"\n    >\n      <strong>\n        Nâng cấp trải nghiệm người dùng với đa dạng tính năng hiện đại\n      </strong>\n    </h3>\n    <p style=\"text-align: justify\">\n      Về mặt tính năng, Redmi Watch 5 Lite được trang bị các tính năng hỗ trợ\n      theo dõi sức khoẻ và luyện tập nổi bật như đo nồng độ oxy (SpO2), theo dõi\n      giấc ngủ, đo nhịp tim và đếm số bước chân. Đặc biệt, Xiaomi smartwatch này\n      còn sở hữu tính năng đo mức độ căng thẳng giúp người dùng theo dõi sức\n      khỏe tinh thần một cách hiệu quả.&nbsp;\n    </p>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/dong-ho-thong-minh-xiaomi-redmi-watch-5-lite-2.jpg\"\n        alt=\"Đồng hồ Xiaomi Redmi Watch 5 Lite - Thiết kế bền bỉ, đa dạng tính năng thông minh\"\n        loading=\"lazy\"\n      />\n    </p>\n    <p style=\"text-align: justify\">\n      Kèm theo đó là hơn 150 chế độ thể thao được tích hợp, giúp đồng hồ có thể\n      hỗ trợ hoạt động luyện tập để đưa ra những số liệu chính xác. Ngoài ra,\n      khả năng nghe gọi qua Bluetooth, trợ lý giọng nói Alexa giúp nâng cao trải\n      nghiệm giao tiếp trên smartwatch Xiaomi này của người dùng.\n    </p>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"huong-dan-ket-noi-dong-ho-xiaomi-redmi-watch-5-lite-voi-dien-thoai\"\n    >\n      <strong>\n        Hướng dẫn kết nối đồng hồ Xiaomi Redmi Watch 5 Lite với điện thoại\n      </strong>\n    </h2>\n    <p style=\"text-align: justify\">\n      Việc kết nối đồng hồ Redmi Watch 5 Lite với điện thoại thông minh khá đơn\n      giản, giúp bạn tận dụng hết các tính năng thông minh mà chiếc đồng hồ này\n      cung cấp. Chi tiết các thao tác kết nối Xiaomi Watch 5 Lite với điện thoại\n      có thể được tiến hành như sau.&nbsp;\n    </p>\n    <ul>\n      <li>\n        <strong>Bước 1 </strong> : Khởi động đồng hồ, chọn ngôn ngữ và tiến hành\n        tải app Mi Fitness về điện thoại.\n      </li>\n      <li>\n        <strong>Bước 2 </strong> : Sau khi đăng nhập thành công vào app Mi\n        Fitness, bạn tiến hành chọn vào mục Thiết bị và nhấn vào dấu cộng trên\n        góc phải màn hình để tiếp tục.\n      </li>\n    </ul>\n    <p style=\"text-align: justify\">\n      <img\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/Mi/dong-ho-thong-minh-xiaomi-redmi-watch-5-lite-4.jpg\"\n        alt=\"Hướng dẫn kết nối đồng hồ Xiaomi Redmi Watch 5 Lite với điện thoại\"\n        loading=\"lazy\"\n      />\n    </p>\n    <ul>\n      <li>\n        <strong>Bước 3 </strong> : Tại bước thứ 3, bạn chọn vào tên của\n        smartwatch Redmi Watch 5 Lite trong danh sách được hiển thị trên màn\n        hình điện thoại. Tiếp đó, bạn chọn vào Ghép đôi trên điện thoại và đồng\n        chọn vào dấu tích trên mặt đồng hồ.&nbsp;\n      </li>\n    </ul>\n    <p style=\"text-align: justify\">\n      Sau khi hoàn thành bước thứ 3 này là bạn đã ghép nối thành công smartwatch\n      với smartphone của mình rồi nhé!\n    </p>\n    <h2\n      style=\"scroll-margin: 154px\"\n      id=\"bang-gia-dong-ho-xiaomi-redmi-watch-5-lite-moi-nhat\"\n    >\n      <strong>Bảng giá đồng hồ Xiaomi Redmi Watch 5 Lite mới nhất</strong>\n    </h2>\n    <p style=\"text-align: justify\"></p>\n    <div style=\"width: 100%\">\n      <table\n        class=\"seo-table seo-product-price table is-bordered is-narrow is-hoverable is-fullwidth\"\n      >\n        <thead>\n          <tr>\n            <td style=\"width: 46%\">\n              <p><strong>Tên sản phẩm</strong></p>\n            </td>\n            <td style=\"width: 27%\">\n              <p><strong>Giá bán</strong></p>\n            </td>\n            <td style=\"width: 27%\">\n              <p><strong>Giá thu cũ lên đời</strong></p>\n            </td>\n          </tr>\n        </thead>\n        <tbody>\n          <tr style=\"width: 100%\">\n            <td style=\"width: 46%\">\n              <p>Đồng hồ thông minh Xiaomi Redmi Watch 5 Lite</p>\n            </td>\n            <td style=\"width: 27%\"><p>1.290.000đ</p></td>\n            <td style=\"width: 27%\"><p>290.000đ</p></td>\n          </tr>\n        </tbody>\n      </table>\n    </div>\n  </div>\n</div>', 1748364129, 1748364129),
(13, 2, 'Redmi Watch 5 Active', '690000', 71, 'Redmi_Watch_5_Active.png', 10, 621000, 99, 5, 1, '<ul>\r\n    <li>Màn hình cảm ứng TFT 1.83 inch lớn, hiển thị rõ ràng và dễ sử dụng</li>\r\n    <li>Pin bền bỉ lên đến 12 ngày sử dụng chỉ với một lần sạc</li>\r\n    <li>Theo dõi sức khỏe 24/7: nhịp tim, SpO₂, giấc ngủ và mức độ căng thẳng</li>\r\n    <li>Hơn 100 chế độ luyện tập hỗ trợ đa dạng nhu cầu thể thao</li>\r\n    <li>Chuẩn kháng nước 5ATM – thoải mái dùng khi bơi lội hoặc rửa tay</li>\r\n</ul>\r\n', '<div id=\"cpsContent\" class=\"cps-block-content\" style=\"max-height: 100000px\">\r\n  <div class=\"ksp-content p-2 mb-2\">\r\n    <h2 class=\"ksp-title has-text-centered\">Đặc điểm nổi bật</h2>\r\n    <div>\r\n      <ul>\r\n        <li>\r\n          Trang bị màn hình LCD 2.0 inch, cho phép hiển thị sắc nét và sống\r\n          động.\r\n        </li>\r\n        <li>\r\n          Đồng hồ hỗ trợ nhiều chế độ thể thao, giúp người dùng theo dõi hoạt\r\n          động tập luyện một cách hiệu quả.\r\n        </li>\r\n        <li>\r\n          Thời lượng pin ấn tượng lên đến 18 ngày, giúp người dùng thoải mái sử\r\n          dụng mà không lo hết pin.\r\n        </li>\r\n        <li>\r\n          Tích hợp tính năng theo dõi sức khỏe, bao gồm đo nhịp tim và giấc ngủ.\r\n        </li>\r\n      </ul>\r\n    </div>\r\n  </div>\r\n  <div id=\"cpsContentSEO\">\r\n    <p style=\"text-align: justify\">\r\n      <strong>Đồng hồ Xiaomi Redmi Watch 5 Active</strong> là một trong những\r\n      mẫu đồng hồ thông minh của Xiaomi với màn hình LCD có kích thước 2 inch.\r\n      Đồng hồ sở hữu màn hình có độ phân giải 360 x 385 pixels ấn tượng. Redmi\r\n      Watch 5 Active có thiết kế thể thao, với vỏ đồng hồ nhẹ nhàng và dây đeo\r\n      được làm từ silicon để tạo cảm giác thoải mái khi đeo.\r\n    </p>\r\n    \r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"vi-sao-nen-mua-dong-ho-xiaomi-redmi-watch-5-active\"\r\n    >\r\n      <strong> Vì sao nên mua đồng hồ Xiaomi Redmi Watch 5 Active? </strong>\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Xiaomi thường cung cấp đồng hồ thông minh với mức giá phải chăng nhưng vẫn\r\n      tích hợp nhiều tính năng hữu ích. Đồng hồ Redmi Watch 5 Active có thể là\r\n      lựa chọn tốt cho những ai tìm kiếm một thiết bị theo dõi sức khỏe và hoạt\r\n      động thể thao hiệu quả mà không phải bỏ ra quá nhiều chi phí.&nbsp;\r\n    </p>\r\n    <ul>\r\n      <li>\r\n        <strong>Thời lượng pin lên đến 18 ngày</strong> ở chế độ cơ bản, lên đến\r\n        12 ngày khi sử dụng với các tác vụ nặng\r\n      </li>\r\n      <li>\r\n        <strong>Hơn 200 mặt đồng hồ độc đáo,</strong> dễ dàng lựa chọn phong\r\n        cách phù hợp với bản thân\r\n      </li>\r\n      <li>\r\n        <strong>Gọi thoại Bluetooth rõ ràng</strong> với công nghệ chống ồn\r\n      </li>\r\n      <li>\r\n        <strong>Tích hợp trợ lý giọng nói Alexa,</strong> dễ dàng kiểm soát tác\r\n        vụ hằng ngày\r\n      </li>\r\n      <li>\r\n        Thiết kế thể thao và tiện dụng: Đồng hồ\r\n        <strong>Xiaomi Redmi Watch 5 Active</strong> có thiết kế thể thao và\r\n        hiện đại, nhẹ nhàng và thoải mái khi đeo&nbsp;\r\n      </li>\r\n      <li>\r\n        <strong>Theo dõi sức khỏe toàn diện:</strong> Với các tính năng theo dõi\r\n        sức khỏe. đồng hồ giúp người dùng quản lý và cải thiện sức khỏe của\r\n        mình.\r\n      </li>\r\n      <li>\r\n        <strong>Hơn 140 chế độ thể thao tích hợp,</strong> cho phép theo dõi\r\n        hiệu suất trong các hoạt động như chạy, đạp xe, bơi lội và nhiều hoạt\r\n        động khác.\r\n      </li>\r\n    </ul>\r\n    <p style=\"text-align: justify\">\r\n      <img\r\n        src=\"https://cdn2.cellphones.com.vn/insecure/rs:fill:0:0/q:90/plain/https://cellphones.com.vn/media/wysiwyg/Watch/Xiaomi/dong-ho-thong-minh-xiaomi-redmi-watch-5-active-2.jpg\"\r\n        alt=\"Vì sao nên mua đồng hồ Xiaomi Redmi Watch 5 Active?\"\r\n        loading=\"lazy\"\r\n      />\r\n    </p>\r\n    <h2\r\n      style=\"scroll-margin: 154px\"\r\n      id=\"so-sanh-xiaomi-redmi-watch-5-active-vs-redmi-watch-3-active\"\r\n    >\r\n      <strong\r\n        >So sánh&nbsp;Xiaomi Redmi Watch 5 Active vs&nbsp;Redmi Watch 3\r\n        Active&nbsp;</strong\r\n      >\r\n    </h2>\r\n    <p style=\"text-align: justify\">\r\n      Xiaomi Redmi Watch 5 Active nổi bật với nhiều cải tiến đáng giá so với\r\n      phiên bản Redmi Watch 3 Active. Thiết kế hiện đại cùng màn hình sắc nét\r\n      mang đến trải nghiệm sử dụng mượt mà hơn, trong khi các tính năng sức khỏe\r\n      và thể thao được nâng cấp đáng kể, giúp người dùng theo dõi hiệu suất một\r\n      cách chính xác. Thời lượng pin kéo dài và khả năng kết nối mượt mà với các\r\n      thiết bị khác cũng là điểm cộng lớn, khẳng định vị thế của Redmi Watch 5\r\n      Active trong phân khúc smartwatch tầm trung.\r\n    </p>\r\n    <p></p>\r\n    <div class=\"table-container\">\r\n      <table\r\n        class=\"seo-table table is-bordered is-hoverable\"\r\n        style=\"min-width: 480px; overflow-x: auto\"\r\n      >\r\n        <thead>\r\n          <tr>\r\n            <td>\r\n              <p><strong>Thông số</strong></p>\r\n            </td>\r\n            <td style=\"width: 35%\">\r\n              <p>\r\n                <strong>Đồng hồ thông minh Xiaomi Redmi Watch 5 Active</strong>\r\n              </p>\r\n            </td>\r\n            <td style=\"width: 35%\">\r\n              <p>\r\n                <strong>Đồng hồ thông minh Xiaomi Redmi Watch 3 Active</strong>\r\n              </p>\r\n            </td>\r\n          </tr>\r\n        </thead>\r\n        <tbody>\r\n          <tr>\r\n            <td>\r\n              <p><strong>Công nghệ màn hình</strong></p>\r\n            </td>\r\n            <td style=\"width: 35%\"><p>LCD</p></td>\r\n            <td style=\"width: 35%\"><p>LCD</p></td>\r\n          </tr>\r\n          <tr>\r\n            <td>\r\n              <p><strong>Kích thước màn hình</strong></p>